		}

		// 계약자 인적사항 수정시 전체 Enable (계약자는 보험료와 무관함.)  , 
		// 2,3일때는 계약변경에서 발송지체크 안하도록 한다.
		if (EnableType == "1" || EnableType == "2" || EnableType == "4") {
			scwin.eventEnable(obj, wconv.trim(EnableType));
		}


		//대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
		if (sAgncYn == "Y") {
