			if (sScanObjtYn == "Y" || (util.getGdsValue("gFaceSalesYN") == "Y")) {
				btn_InsDesign.setStyle("display", "none");
			}
		}

		// 상품소개 동의 여부 체크(최초 화면을 띄울때도 체크)
		btn_GoodsIntro.setUserData("currClass", "btn_nml1 bul");
