	/**
	 * @function	search
	 * @desc		고객정보, 주소, 연락처 검색
	 * @param		custNo : 주민등록번호/사업자 번호
	 * search(custNo, seqNo,nPlrAddrSeq,nPlrTelSeq,nPlrDMAddrSeq);
	 **/
	scwin.search = function(custNo, seqNo, sAddrType, nAddrSeq, nTelSeq, sSendType, nSendSeq) {
		// Dataset 초기화
		wconv.clear(DsWs_UserInfo);
		wconv.clear(DsWs_TelInfo);
		wconv.clear(DsWs_TelInfo_1);
		wconv.clear(DsWs_AddrHome);
		wconv.clear(DsWs_AddrHome_1);
		wconv.clear(DsWs_AddrOffice);
		wconv.clear(DsWs_AddrEtc);
		wconv.clear(DsWs_AddrTemp);
		wconv.clear(DsWs_CUSBA01);
		wconv.clear(DsWs_DMAddr);
		wconv.clear(Ds_DMAddr);
		wconv.clear(ds_JobCod); //직업급수 
		wconv.clear(DsWs_VIP);
		wconv.clear(DsWs_DB);
		wconv.clear(DsWs_CustEtcInfo);	//cbs - 영업기타정보

		//DB 접근여부 확인
		// VIP 체크   
	/* OLD S */    
	//    SOBJ_VIP.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정 
	//    var MethodObj = gfn_CreateMethodObj(SOBJ_VIP, "com.inswave.customer.task.CustTask2", "selectVIPCustYN", 0, 0);

		// 주민등록번호/사업자번호 세팅
	//    gfn_SetParam(MethodObj, "sCustNo", custNo);
	//    gfn_CallService(SOBJ_VIP, DOBJ_VIP, MethodObj);
	/* OLD E */
	/* NEW S */
		var MethodObj = new callService("com.inswave.customer.task.CustTask2", "selectVIPCustYN", 0, 0);
		MethodObj.setId("selectVIPCustYN");
		MethodObj.setParam("sCustNo", custNo);
		MethodObj.setUrl("/common/CBSO002PController.do");
		MethodObj.setXPath("//*/CUSAA01");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		//util.interfaceKey(_tranSync.result.serviceDs);
		DsWs_VIP.setJSON(_tranSync.result.serviceDs.data[0].CUSAA01);
	/* NEW E */

		var sVIPYN = DsWs_VIP.getCellData(0, "sVIPYN");
		if (sVIPYN == "Y") {
			VIP.setValue("VIP");
		} else {
			VIP.setValue("");
		}

	/* OLD S */
		//SOBJ_control.WSDL = gWsdlCustomer;	// 서비스 Obj WSDC 설정
	//    SOBJ_control.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정 
	//    var MethodObj = gfn_CreateMethodObj(SOBJ_control, "com.inswave.longterm.cst.cst.task.CAA070Task", "getCustomerInfo", 0, 0);

		// 주민등록번호/사업자번호 세팅
	//    gfn_SetParam(MethodObj, "sCustNo", custNo);
		// 고객, 연락처, 주소 순번 세팅
	//    gfn_SetParam(MethodObj, "nSeqNoCust", seqNo);
	//    gfn_SetParam(MethodObj, "nSeqNoTel", "0");
	//    gfn_SetParam(MethodObj, "nSeqNoAddr", "0");
	//    gfn_SetParam(MethodObj, "Cmb_SendType", sSendType);
