	scwin.Cmb_SendType_onviewchange = function(info) {
		var newValue = info.newValue;
		var newIndex = info.newSelectedIndex;
		scwin.setSendAddr(newIndex);
	};

 /**
   * @function	setSendAddr
   * @desc		발송지 주소 세팅
   * @param      
   * 				dsStr : DataSet 명
   *  code 1:  자택 주소 :, 2: 직장주소,  3: 기타 주소 , 4: 임시주소 
   **/
	scwin.setSendAddr = function(code) {
      var dsStr = ""; // 주소 DataSet 명
      var addrStr = ""; // 주소 구분 명
      //if (code == 0) {
      // 발송지 주소 초기화
      Mdt_SendZip.setValue("");
      Mdt_SendAddr.setValue("");
      //} else {
      // 해당 주소 DataSet
      if (code == "1") {
          // 자택 주소 
          dsStr = "Ds_AddrHome";
      } else if (code == "2") {
          // 직장주소 
          dsStr = "Ds_AddrOffice";
      } else if (code == "3") {
          // 기타 주소 
          dsStr = "Ds_AddrEtc";
      } else {
          dsStr = "Ds_AddrTemp"; // 임시주소 
      }
      addrStr = Cmb_SendType.getText();	//Ds_SendType.getCellData(code, "sCodeName");
      if (wconv.length($w.data[dsStr].getCellData(0, "sZip1")) < 1 || wconv.length($w.data[dsStr].getCellData(0, "sZip2")) < 1 || wconv.length($w.data[dsStr].getCellData(0, "sAdrs")) < 1 || wconv.length(wconv.trim($w.data[dsStr].getCellData(0, "sAdrsAdd"))) < 1) {
          Cmb_SendType.setValue("");
          wprom.alert("먼저 " + addrStr + "주소를 입력해 주세요.");
      } else {
          Mdt_SendZip.setValue($w.data[dsStr].getCellData(0, "sZip1") + $w.data[dsStr].getCellData(0, "sZip2"));
          Mdt_SendAddr.setValue($w.data[dsStr].getCellData(0, "sAdrs") + " " + $w.data[dsStr].getCellData(0, "sAdrsAdd"));
      }
	  editBln = "true"; //}
	};

	scwin.Cmb_Email1_OnChanged = function() {
	    //eMailDomainAppend(Edt_sEmail1, Cmb_Email1);
		var sEmail = Edt_sEmail1.getValue();
		var cmbEmail = Cmb_Email1.getValue();
		var cmbEmailId = Cmb_Email1.getSelectedIndex();
		var cmbEmailNm = Ds_Email.getCellData(cmbEmailId-1, "sCodeNameEng");

		if (cmbEmail != "17") {  //17:사용안함
			var nIndex = sEmail.indexOf("@", 0);

			if (nIndex > -1) {
			   sEmail = wconv.substr(sEmail, 0, nIndex);
			}

			sEmail = sEmail + cmbEmailNm;
		} else {
			sEmail = cmbEmailNm;
		}
		Edt_sEmail1.setValue( sEmail );
	};

  //---------------------------------------------------------------------------
  // function 명 : lfn_getCUSAA42Info()
  // 내용        : 가입설계 동의여부 확인 
  //---------------------------------------------------------------------------
  scwin.lfn_getCUSAA42Info = function(insrdID) {
      //신용정보 동의 23조 동의여부 확인	 
      wconv.clear(DsWs_Search42);
      var RejectYN = "";
      // 가입설계 동의 여부 체크 
  /* OLD S */    
  //    SOBJ_Search.WSDL = gds.gWsdlBasis;
  //    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.customer.task.CustTask", "getCUSAA42Info", 0, 0);
  //    gfn_SetParam(MethodObj, "sCustNo", insrdID);
  //    gfn_SetParam(MethodObj, "sProductType", "3");
  //    var result = gfn_CallService(SOBJ_Search, DOBJ_Search, MethodObj);
  /* OLD E */
  /* NEW S */
  	var MethodObj = new callService("com.inswave.customer.task.CustTask", "getHandlerCUSAA42Info", 0, 0);
  	MethodObj.setId("getHandlerCUSAA42Info");
  	MethodObj.setParam("sCustNo", insrdID);
  	MethodObj.setParam("sProductType", "3");
  	MethodObj.setParam("sHandlerID", sHandlerID);
  	MethodObj.setUrl("/common/CBSO002PController.do");
  	MethodObj.setXPath("//*/CUSAA42");
  	MethodObj.addXPath("//WARNING");
  	MethodObj.sync();
  	MethodObj.send();

  	util.interfaceKey(_tranSync.result.serviceDs);
    DsWs_Search42.setJSON(_tranSync.result.serviceDs.data[0].CUSAA42);
  /* NEW E */

      //trace("parent.sPlanNo="+left(parent.sPlanNo,1)+"EnableType="+EnableType+"RejectYN="+DsWs_Search42.GetColumn(0, "sAgmReq")+IsExistVar(parent.sPlanNo, true));
      if (DsWs_Search42.getRowCount() > 0 && DsWs_Search42.getCellData(0, "result") > 0) {
          if (DsWs_Search42.getCellData(0, "sAgmReq") == "Y") {
              RejectYN = "Y"; //유효일자가 지난경우 다시 받기. 해당 칼럼이 다 체크해줌
              //2016.09.05(N1609-00035)
              //if (wconv.IsExistVar(inJSON.sPlanNo) != true) {
                  if (wconv.left(sPlanNo, 1) == "N") {
                      RejectYN = "";
                  }
              //}
          }
      } else {
          RejectYN = "Y"; //가입동의 거부
      }
      return RejectYN;
  };	

  //---------------------------------------------------------------------------
  // function 명 : lfn_getCUSAA0441Info()
  // 내용        : 상품설계  동의여부 확인 
  //---------------------------------------------------------------------------
  scwin.lfn_getCUSAA0441Info = function(insrdID) {
      var RejectYN = "";
      // 가입설계 동의 여부 체크 
      wconv.clear(DsWs_Search41);
      wconv.clear(DsWs_Search04);

/* OLD S */      
//      SOBJ_Search.WSDL = gds.gWsdlBasis;
//      var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.customer.task.CustTask", "getCUSAA0441Info", 0, 0);
//      gfn_SetParam(MethodObj, "sCustNo", insrdID);
//      var result = gfn_CallService(SOBJ_Search, DOBJ_Search, MethodObj);
/* OLD E */
/* NEW S */
	var MethodObj = new callService("com.inswave.customer.task.CustTask", "getHandlerCUSAA0441Info", 0, 0);
	MethodObj.setId("getHandlerCUSAA0441Info");
	MethodObj.setParam("sCustNo", insrdID);
	MethodObj.setParam("sHandlerID", sHandlerID);
	MethodObj.setUrl("/common/CBSO002PController.do");
	MethodObj.setXPath("//*/CUSAA04");
	MethodObj.addXPath("//*/CUSAA41"); // 여러개의 데이타셋으로 조회하는 경우 추가한다
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();    

	util.interfaceKey(_tranSync.result.serviceDs);
    DsWs_Search04.setJSON(_tranSync.result.serviceDs.data[0].CUSAA04);
    DsWs_Search41.setJSON(_tranSync.result.serviceDs.data[1].CUSAA41);
/* NEW E */      

      //trace(replace(gfn_XmlEncoder(DOBJ_Search.Value), "><", ">\n<"));
      //trace(DsWs_Search04.SaveXML());
      //trace(DsWs_Search41.SaveXML());
      //trace(DsWs_Search04.SaveXML());
      RejectYN = "Y"; //가입동의 거부(Y인 경우 동의 필요)
      if (DsWs_Search04.getRowCount() > 0 && DsWs_Search41.getRowCount() > 0) {
          if (DsWs_Search41.getCellData(0, "sAgmReq") == "N" && DsWs_Search04.getCellData(0, "sAgmReq") == "N") {
              RejectYN = "N";
          }
      } else {
          RejectYN = "Y"; //가입동의 거부(Y인 경우 동의 필요)
      }


      // 상품동의 여부 체크 (전화번호 및 메일 변경시) 2014.04.01 제도개정 N1403-00273
      //if(sGoodAgreeFlag == "Y" && length(sGoodsInTroYN)> 0 ){
      //    sGoodAgreeFlag = "N";
      //}
      return RejectYN;
  };

  	/**
	 * @function	Btn_Save_OnClick
	 * @desc		
	 *   고객정보, 주소정보, 연락처정보 저장 버튼 클릭 작업 
	 **/
	scwin.Btn_Save_onclick_Async = function() { //완료
 		util.eventAsyncCall(this, scwin.Btn_Save_onclick, arguments);
	};

	scwin.Btn_Save_onclick = function() {
		// 가입설계 동의 여부 체크
		if (scwin.lfn_getCUSAA42Info(Ds_UserInfo.getCellData(0, "sCustNo")) == "Y") {/**/
			wprom.alert("가입설계 동의 여부를 입력하세요.");
			btn_InsDesign.changeClass(btn_InsDesign.getUserData("currClass"), "btn_nml1 red");
			btn_InsDesign.setUserData("currClass", "btn_nml1 red");
//			btn_InsDesign.setStyle("color", "red");
//			btn_InsDesign.setStyle("font", "굴림 9 Bold");
			return;
		} else {
			btn_InsDesign.changeClass(btn_InsDesign.getUserData("currClass"), "btn_nml1 bul");
			btn_InsDesign.setUserData("currClass", "btn_nml1 bul");
//			btn_InsDesign.setStyle("color", "black");
//			btn_InsDesign.setStyle("font", "굴림 9");
		}

		// 상품소개 동의 여부 체크 
		if ((sImsiYN == "N") && (sGoodAgreeFlag == "N")) {
			//Y 인경우는 skip
			if (scwin.lfn_getCUSAA0441Info(Ds_UserInfo.getCellData(0, "sCustNo")) == "Y") {
				wprom.alert("상품소개 동의 여부를 입력하세요.");
				btn_GoodsIntro.changeClass(btn_GoodsIntro.getUserData("currClass"), "btn_nml1 red");
				btn_GoodsIntro.setUserData("currClass", "btn_nml1 red");
//				btn_GoodsIntro.setStyle("color", "red");
//				btn_GoodsIntro.setStyle("font", "굴림 9 Bold");
				return;
			} else {
				btn_GoodsIntro.changeClass(btn_GoodsIntro.getUserData("currClass"), "btn_nml1 bul");
				btn_GoodsIntro.setUserData("currClass", "btn_nml1 bul");
//				btn_GoodsIntro.setStyle("color", "black");
//				btn_GoodsIntro.setStyle("font", "굴림 9");
			}
		}

		scwin.userInfoSave(); //Tab_Req.Tab_Step1.Div_Step1.CallScript("Button3_OnClick(obj)");
	};

	/**
	 * @function	userInfoSave
	 * @desc		입력값 체크
	 *              고객정보, 주소정보, 연락처정보 저장
	 * @return		true/false
	 **/
	scwin.userInfoSave = function() {
		editBln = "false"; // 고객 정보 (기본, 주소, 연락처)를 입력/수정 여부 확인
		/* 입력값 체크 시작 */
		//check_telno(trim(dataTab.tab1.Edt_HomeTel1.text));
		//check_telno(trim(dataTab.tab1.Edt_OfficeTel1.text));
		//check_cellphone(trim(dataTab.tab1.Edt_CellPhone1.text));
		if (wconv.length(wconv.trim(Ds_UserInfo.getCellData(0, "sCustNo"))) == "") {
			wprom.alertF("고객번호는 필수입력 입니다.", {}, Edt_CustNo);
			return false;
		}


		// 고객명 체크
		if (wconv.length(wconv.trim(Edt_Name.getValue())) < 2) {
			wprom.alertF("고객명은 필수입력 입니다.", {}, Edt_Name);
			return false;
		}


		// 임시 체크 및 24조 신용정보 데이터 조회 
		if ((Ds_CUSBA01.getCellData(0, "sCustNo") == "") && chk_imsi.getValue() == "0") {
			wprom.alertF("반드시 신용정보 조회를 하셔야 합니다.", {}, btn_InsDesign);
			return false;
		}

		// 고객번호 체크
		if (wconv.trim(Edt_sJobCode.getValue()) == "" ) {
			wprom.alert("직업코드는 필수입력 입니다.");
			return false;
		}


		//국적 입력 필수 
		if (!scwin.lfn_NtlCheck()) {
			return false;
		}

	/* 2017.09.05 */
		// 전화번호 체크 
		// 자택, 직장, 휴대전화, 기타 하나만 필수 입력 
		var inValue1 = wconv.trim(Edt_HomeTel1.getValue()) + wconv.trim(Edt_HomeTel2.getValue()) + wconv.trim(Edt_HomeTel3.getValue());
		var inValue2 = wconv.trim(Edt_OfficeTel1.getValue()) + wconv.trim(Edt_OfficeTel2.getValue()) + wconv.trim(Edt_OfficeTel3.getValue());
		var inValue3 = wconv.trim(Edt_CellPhone1.getValue()) + wconv.trim(Edt_CellPhone2.getValue()) + wconv.trim(Edt_CellPhone3.getValue());
		var inValue4 = wconv.trim(Edt_CellPhone1.getValue()) + wconv.trim(Edt_CellPhone2.getValue()) + wconv.trim(Edt_CellPhone3.getValue());
		var totalValue = inValue1 + inValue2 + inValue3 + inValue4;

		if (wconv.length(wconv.trim(totalValue)) < 2) {
			wprom.alertF("전화번호(자택, 직장, 휴대전화, 기타) 중 하나는 필수입력 입니다.", Edt_HomeTel1);
			return false;
		}
		if (!scwin.phoneCheck(Edt_HomeTel1, Edt_HomeTel2, Edt_HomeTel3, "전화번호(자택)를")) {
			return false;
		}
		if (!scwin.phoneCheck(Edt_CellPhone1, Edt_CellPhone2, Edt_CellPhone3, "전화번호(휴대전화)를")) {
			return false;
		}
		if (!scwin.phoneCheck(Edt_Fax1, Edt_Fax2, Edt_Fax3, "전화번호(FAX)를")) {
			return false;
		}
		if (!scwin.phoneCheck(Edt_OfficeTel1, Edt_OfficeTel2, Edt_OfficeTel3, "전화번호(직장)를")) {
			return false;
		}
		if (!scwin.phoneCheck(Edt_EtcTel1, Edt_EtcTel2, Edt_EtcTel3, "전화번호(기타)를")) {
			return false;
		}
		/**/
		if ((wconv.length(wconv.trim(Edt_EtcTel1.getValue())) > 0) &&
			(wconv.length(wconv.trim(Edt_EtcTel2.getValue())) > 0) &&
			(wconv.length(wconv.trim(Edt_EtcTel3.getValue())) > 0) &&
			(Cmb_EtcType.getValue() == "0")) {
			wprom.alertF("전화번호(기타) 구분을 선택해주세요!!", {}, Cmb_EtcType);
			return false;
		}

/* cbs - 임시 isCorrectEmail
		// 이메일 체크 2009-07-07   Edt_sEmail1.getValue()  ="사용안함" 데이터  이메일 체크 안함 
		if ((Edt_sEmail1.getValue() != "17") && (wconv.length(wconv.trim(Edt_sEmail1.getValue())) > 1)) {
			if (!isCorrectEmail(Edt_sEmail1.getValue())) {
				Edt_sEmail1.focus();
				return false;
			}
		}
*/		

		// 주소체크
		if (wconv.length(wconv.trim(Edt_1_Zip1.getValue())) < 1) {
			wprom.alertF("자택주소는 필수입력 입니다.", {}, Edt_1_Zip1);
			return false;
		} else {
			if (!scwin.addrCheck(Edt_1_Zip1, Edt_1_Zip2, Edt_1_sAdrs1, Edt_1_AdrsAdd, "자택주소를")) {/**/
				return false;
			}
		}

		// 직장 주소체크
		if (!scwin.addrCheck(Edt_2_Zip1, Edt_2_Zip2, Edt_2_sAdrs1, Edt_2_AdrsAdd, "직장주소를")) {
			return false;
		}

		// 가타 주소체크
		if (!scwin.addrCheck(Edt_3_Zip1, Edt_3_Zip2, Edt_3_sAdrs1, Edt_3_AdrsAdd, "기타주소를")) {
			return false;
		}

		// 임시 주소 체크
		if (!scwin.addrCheck(Edt_4_Zip1, Edt_4_Zip2, Edt_4_sAdrs1, Edt_4_AdrsAdd, "임시주소를")) {
			return false;
		}

		// 상품소개 동의 반드시 받아야 되는 경우 체크 
	    if (sGoodAgreeFlag == "Y") {//N 인경우는 skip
	        wprom.alert("전화번호 및 이메일이 변경되어 상품소개 동의 여부를 다시 받으세요.");
	        btn_GoodsIntro.setDisabled(true);
	        return false;
	    }

		//활성화 구분이 3일때는 발송지주소 체크하지 않는다. 
		if (EnableType != "3") {
			// 발송지 주소 체크 (청약쪽(권기완)  요청에 의해 주석처리 -> 다시해제  )
			if (wconv.length(wconv.trim(Mdt_SendZip.getValue())) < 1) {
				//wprom.alert("발송지 주소는 필수입력 입니다.");
				wprom.alertF("발송지 주소는 필수입력 입니다.", {}, Cmb_SendType);
				return false;
			}
		}

		//trace("\n\n ************************ Ds_AddrHome ************************\n" + Ds_AddrHome.SaveXML());
		//trace("\n\n ************************ Ds_AddrOffice ************************\n" + Ds_AddrOffice.SaveXML());
		//trace("\n\n ************************ Ds_AddrEtc ************************\n" + Ds_AddrEtc.SaveXML());
		//trace("\n\n ************************ Ds_AddrTemp ************************\n" + Ds_AddrTemp.SaveXML());
		// DataSet에 cType 추가
//cbs - 동적으로 column추가 지원안됨. DataList에 직접 해당 column추가(Ds_UserInfo)	 	
//		util.insertColumn(Ds_UserInfo, [["cType","","text"]]);
//		util.insertColumn(Ds_UserInfo, [["sCreateID","","text"]]);
//		util.insertColumn(Ds_AddrHome, [["cType","","text"]]);
//		util.insertColumn(Ds_AddrHome, [["sUserID","","text"]]);
//		util.insertColumn(Ds_AddrOffice, [["cType","","text"]]);
//		util.insertColumn(Ds_AddrOffice, [["sUserID","","text"]]);
//		util.insertColumn(Ds_AddrEtc, [["cType","","text"]]);
//		util.insertColumn(Ds_AddrEtc, [["sUserID","","text"]]);
//		util.insertColumn(Ds_AddrTemp, [["cType","","text"]]);
//		util.insertColumn(Ds_AddrTemp, [["sUserID","","text"]]);
//		util.insertColumn(Ds_TelInfo, [["cType","","text"]]);
//		util.insertColumn(Ds_TelInfo, [["sUserID","","text"]]);

		//trace("GetRowType : cType "+Ds_UserInfo.GetRowType(0));
		// Ds_UserInfo의 GetRowType을 확인후 'ctype' 컬럼에 세팅
		Ds_UserInfo.setCellData(0, "cType", scwin.convRowType(Ds_UserInfo.getRowStatus(0)));

		//행의 상태코드 (초기상태:"R", 갱신:"U", 삽입:"C", 삭제:"D", 삽입 후 삭제:"V")

		// Ds_UserInfo의 GetRowType이 'insert'인 경우 해당 컬럼 세팅
		if (Ds_UserInfo.getRowStatus(0) == "C") {
			// Ds_UserInfo의 'nSeqNo'를 '0'으로 세팅
			Ds_UserInfo.setCellData(0, "nSeqNo", "0");


			// Ds_UserInfo의 'sCustType'을 세팅 
			// (주민등록번호 : 1, 사업자번호 : 2, 기타 : 3)
			if ( wconv.length(Ds_UserInfo.getCellData(0,"sCustNo")) == 10) {
				Ds_UserInfo.setCellData(0, "sCustType", "2");
			} else if ( wconv.length(Ds_UserInfo.getCellData(0,"sCustNo")) == 13) {
				Ds_UserInfo.setCellData(0, "sCustType", "1");
			} else {
				Ds_UserInfo.setCellData(0, "sCustType", "3");
			}

			// Ds_UserInfo의 'sClass'을 '1'로 세팅
			Ds_UserInfo.setCellData(0, "sClass", "1"); // 고객구분 : 고객 데이터만 
			Ds_UserInfo.setCellData(0, "sContactPath", "1"); //  최초 접촉 경로 1: 
			Ds_UserInfo.setCellData(0, "sAffiliatedConcern", util.getGdsValue("gAffiliatedConcern")); // 제휴채널 처리
			//고객 정보 저장 
			gfn_LogCreditInfo("", "", "I", Ds_UserInfo.getCellData(0, "sCustNo"));
			/**/
		} else {
			if (wconv.length(Ds_UserInfo.getCellData(0, "sCustType")) < 1) {
				// Ds_UserInfo의 'sCustType'을 세팅 
				// (주민등록번호 : 1, 사업자번호 : 2, 기타 : 3)
				if ( wconv.length(Ds_UserInfo.getCellData(0,"sCustNo")) == 10) {
					Ds_UserInfo.setCellData(0, "sCustType", "2");
				} else if ( wconv.length(Ds_UserInfo.getCellData(0,"sCustNo")) == 13) {
					Ds_UserInfo.setCellData(0, "sCustType", "1");
				} else {
					Ds_UserInfo.setCellData(0, "sCustType", "3");
				}
			}


			//고객 정보 수정 ()
			gfn_LogCreditInfo("", "", "U", Ds_UserInfo.getCellData(0, "sCustNo"));
		}
		Ds_UserInfo.setCellData(0, "sUserID", util.getGdsValue("gUserID")); //   입력자  
		Ds_UserInfo.setCellData(0, "sCreateID", util.getGdsValue("gUserID")); //   입력자

		//cbs - 영업고객정보 추가
		Ds_CustEtcInfo.setCellData(0, "cType", scwin.convRowType(Ds_CustEtcInfo.getRowStatus(0)));
		Ds_CustEtcInfo.setCellData(0, "sHandlerID", sHandlerID); //   취급자
		Ds_CustEtcInfo.setCellData(0, "sUserID", util.getGdsValue("gUserID")); //   입력자 
		Ds_CustEtcInfo.setCellData(0, "sCustNo", custNo);
		Ds_CustEtcInfo.setCellData(0, "sHandlerID", sHandlerID);

		// 고객의 전화번호 정보가 입력된 경우 해당 컬럼 세팅	
		if (wconv.length(totalValue) > 2) {
			Ds_TelInfo.setCellData(0, "cType", scwin.convRowType(Ds_TelInfo.getRowStatus(0)));
			Ds_TelInfo.setCellData(0, "sCustNo", Ds_UserInfo.getCellData(0, "sCustNo"));
			Ds_TelInfo.setCellData(0, "nSeqNo", "0");
		} else {
			Ds_TelInfo.setCellData(0, "cType", "none");
		}
		Ds_TelInfo.setCellData(0, "sUserID", util.getGdsValue("gUserID")); //   입력
		Ds_TelInfo.setCellData(0, "sHandlerID", sHandlerID); //   취급자

		// 고객의 주소(자택) 정보가 입력된 경우 해당 컬럼 세팅
		if (wconv.length(wconv.trim(Ds_AddrHome.getCellData(0, "sZip1"))) > 0) {
			Ds_AddrHome.setCellData(0, "cType", scwin.convRowType(Ds_AddrHome.getRowStatus(0)));
			Ds_AddrHome.setCellData(0, "sCustNo", Ds_UserInfo.getCellData(0, "sCustNo"));
			Ds_AddrHome.setCellData(0, "sAdrsType", "1");
			Ds_AddrHome.setCellData(0, "nSeqNo", "0");
		} else {
			Ds_AddrHome.setCellData(0, "cType", "none");
		}
		Ds_AddrHome.setCellData(0, "sUserID", util.getGdsValue("gUserID")); //   입력자
		Ds_AddrHome.setCellData(0, "sHandlerID", sHandlerID); //   취급자 

		// 고객의 주소(직장) 정보가 입력된 경우 해당 컬럼 세팅
		if (wconv.length(wconv.trim(Ds_AddrOffice.getCellData(0, "sZip1"))) > 0) {
			Ds_AddrOffice.setCellData(0, "cType", scwin.convRowType(Ds_AddrOffice.getRowStatus(0)));
			Ds_AddrOffice.setCellData(0, "sCustNo", Ds_UserInfo.getCellData(0, "sCustNo"));
			Ds_AddrOffice.setCellData(0, "sAdrsType", "2");
			Ds_AddrOffice.setCellData(0, "nSeqNo", "0");
		} else {
			Ds_AddrOffice.setCellData(0, "cType", "none");
		}
		Ds_AddrOffice.setCellData(0, "sUserID", util.getGdsValue("gUserID")); //   입력자
		Ds_AddrOffice.setCellData(0, "sHandlerID", sHandlerID); //   취급자 

		// 고객의 주소(기타) 정보가 입력된 경우 해당 컬럼 세팅
		if (wconv.length(wconv.trim(Ds_AddrEtc.getCellData(0, "sZip1"))) > 0) {
			Ds_AddrEtc.setCellData(0, "cType", scwin.convRowType(Ds_AddrEtc.getRowStatus(0)));
			Ds_AddrEtc.setCellData(0, "sCustNo", Ds_UserInfo.getCellData(0, "sCustNo"));
			Ds_AddrEtc.setCellData(0, "sAdrsType", "3");
			Ds_AddrEtc.setCellData(0, "nSeqNo", "0");
		} else {
			Ds_AddrEtc.setCellData(0, "cType", "none");
		}
