			if (wconv.trim(Ds_sa.getCellData(0, "addrEtcInfo")) != "" ) {
				ds_long_mPlr.setCellData(0, "nEtcAdrsChangeNo", Ds_sa.getCellData(0, "addrEtcInfo"));
				if (Cmb_SendType.getValue() == "3") {
