		//trace("Ds_GENAA12::::::::::::::::::::::::"+Ds_GENAA12.SaveXML());
		/*-----------------------------------------------------------------------------------------------*/
		//parent접근불가. returnParam으로 변경
		var rtnJSON = {};

		if (screenId == "") {
			//parent.ds_mIsdInf01.Copy(ds_tmp_mPlr);
