//국적팝업	
	scwin.Btn_Ntl_onclick = function() {
		var pro = Promise.resolve();
		var indata;

		if (wconv.trim(wconv.length(Edt_sNationName.getValue())) > 1) {
			indata = {	"sCdNameFlag" : "Name",
						"sSerchKey" : Edt_sNationName.getValue()
		 			 };
		} else {
			indata = {	"sCdNameFlag" : "Cd",
						"sSerchKey" : Edt_sNation.getValue()
					 };
		}

		var popInfo = {
						"width"     : 412,
						"height"    : 430,  
						"indata"    : indata,
						"title"     : "국적 조회",
						"callback"  : scwin.popupCallback
					  };

		pro = pro.then(function(rv) {
			return wprom.popupOpen("/ui/cbso/CBSO027P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();
console.log("====CBSO027P promise close \n"+JSON.stringify(rtnValue));
			if (rtnValue != "") {
				var NtnlInfo = rtnValue.split("^");
				Edt_sNation.setValue(NtnlInfo[0]);
				Edt_sNationName.setValue(NtnlInfo[1]);

				//국적 체크
				scwin.lfn_NtlCheck();
			}
		});
	};

  //국적 validation check
  scwin.lfn_NtlCheck = function() {
  	  if ((sNtlCheck.indexOf(wconv.mid(Edt_CustNo.getValue(), 6, 1)) > -1) && (Edt_sNation.getValue() != "KR")) {
          wprom.alert("국적을 확인하세요");
          Edt_sNation.setValue("");
          Edt_sNationName.setValue("");
          return false;
      } else if ((sNtlCheck.indexOf(wconv.mid(Edt_CustNo.getValue(), 6, 1)) == -1) && (Edt_sNation.getValue() == "KR")) {      
          wprom.alert("국적을 확인하세요");
          Edt_sNation.setValue("");
          Edt_sNationName.setValue("");
          return false;
      } else if (wconv.length(Edt_sNation.getValue()) < 2) {
          wprom.alert("국적을 확인하세요");
          Edt_sNation.setValue("");
          Edt_sNationName.setValue("");
          return false;
      }
      return true;
  };

  /**
   * @function	phoneCheck
   * @desc		전화번호 체크
   * @param      
   * 				tel1Cpt : 전화번호 컴포넌트
   * 				tel2Cpt : 전화번호 컴포넌트
   * 				tel3Cpt : 전화번호 컴포넌트
   * 				errStr  : 에러 메세지
   * @return		true/false
   **/
  scwin.phoneCheck = function(tel1Cpt, tel2Cpt, tel3Cpt, errStr) {
      if ((wconv.length(wconv.trim(tel1Cpt.getValue())) > 0) ||
          (wconv.length(wconv.trim(tel2Cpt.getValue())) > 0) ||
          (wconv.length(wconv.trim(tel3Cpt.getValue())) > 0)) {
          if (wconv.length(wconv.trim(tel1Cpt.getValue())) < 2) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, tel1Cpt);
              return false;
          }
          if (wconv.length(wconv.trim(tel2Cpt.getValue())) < 3) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, tel2Cpt);
              return false;
          }
          if (wconv.length(wconv.trim(tel3Cpt.getValue())) != 4) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, tel3Cpt);
              return false;
          }
      }

      if (wconv.length(tel1Cpt.getValue() + tel2Cpt.getValue() + tel3Cpt.getValue()) > 0) {
          if (isValidPhone(tel1Cpt.getValue() + tel2Cpt.getValue() + tel3Cpt.getValue())) {
              return true;
          } else {
              //alert(errStr + " 무효한 번호입니다. \n재입력해 주세요");
              return false; //alert("무효");
          }
      }
      return true;
  };  

  /**
   * @author      김용석 
   * @function	addrCheck
   * @desc		주소 체크
   * @param      
   * 				addr1Cpt : 우편번호 컴포넌트
   * 				addr2Cpt : 우편번호 컴포넌트
   * 				addr3Cpt : 주소 컴포넌트
   * 				addr4Cpt : 주소 컴포넌트
   * 				errStr  : 에러 메세지
   * @return		true/false
   **/ 
  scwin.addrCheck = function(addr1Cpt, addr2Cpt, addr3Cpt, addr4Cpt, errStr) {
      if ((wconv.length(wconv.trim(addr1Cpt.getValue())) > 0) ||
          (wconv.length(wconv.trim(addr2Cpt.getValue())) > 0) ||
          (wconv.length(wconv.trim(addr3Cpt.getValue())) > 0) ||
          (wconv.length(wconv.trim(addr4Cpt.getValue())) > 0)) {
          if (wconv.length(wconv.trim(addr1Cpt.getValue())) != 3) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, addr1Cpt);
              return false;
          }
          if (wconv.length(wconv.trim(addr2Cpt.getValue())) < 2) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, addr2Cpt);
              return false;
          }
          if (wconv.length(wconv.trim(addr3Cpt.getValue())) < 3) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, addr3Cpt);
              return false;
          }
          if (wconv.length(wconv.trim(addr4Cpt.getValue())) < 3) {
              wprom.alertF(errStr + " 바르게 입력해 주세요.", {}, addr4Cpt);
              return false;
          }
      }
      return true;
  };  

	scwin.check_telno = function(telno1) {
		if ((telno1 != "02" && telno1 != "032" && telno1 != "031" && telno1 != "033" && telno1 != "043" && telno1 != "042" && telno1 != "063" && telno1 != "053" && telno1 != "052" && telno1 != "051" && telno1 != "055" && telno1 != "061" && telno1 != "062" && telno1 != "064" && telno1 != "070") && wconv.length(telno1) > 0) {
			wprom.alert("지역번호를 확인하세요");
			return false;
		}
	};

	scwin.check_cellphone = function(telno1) {
		if ((telno1 != "016" && telno1 != "017" && telno1 != "018" && telno1 != "019" && telno1 != "011" && telno1 != "010") && wconv.length(telno1) > 0) {
			wprom.alert("핸드폰 앞번호를 확인하세요");
			return false;
		}
	};

	scwin.Edt_HomeTel1_onblur = function(e) {
		scwin.check_telno(Edt_HomeTel1.getValue());
	};

	scwin.Edt_OfficeTel1_onblur = function(e) {
		scwin.check_telno(Edt_OfficeTel1.getValue());
	};

	scwin.Edt_Fax1_onblur = function(e) {
		scwin.check_telno(Edt_Fax1.getValue());
	};

	scwin.Edt_CellPhone1_onblur = function(e) {
		scwin.check_cellphone(Edt_CellPhone1.getValue());
	};

	scwin.Edt_EtcTel1_onblur = function(e) {
		var telno1 = wconv.trim(Edt_EtcTel1.getValue());
		if ((telno1 != "02" && telno1 != "032" && telno1 != "031" && telno1 != "033" && telno1 != "043" && telno1 != "042" && telno1 != "063" && telno1 != "053" && telno1 != "052" && telno1 != "051" && telno1 != "055" && telno1 != "061" && telno1 != "062" && telno1 != "064" && telno1 != "016" && telno1 != "017" && telno1 != "018" && telno1 != "019" && telno1 != "011" && telno1 != "010") && wconv.length(telno1) > 0) {
			wprom.alert("지역(핸드폰)번호를 확인하세요");
			return false;
		}
	};

	//MiPlatform GetRowType <- getRowStatus 로 변경
	//Mi Record의 RowType 'Insert', 'Update', 'Delete', 'Logical', 'Normal' 중 하나의 값을 가집니다.
	//DataList 행의 상태코드 (초기상태:"R", 갱신:"U", 삽입:"C", 삭제:"D", 삽입 후 삭제:"V")
	scwin.convRowType = function(rowStatus) {
		var rtnRowType = "none";
		if (rowStatus == "C") {
			rtnRowType = "insert";
		} else if (rowStatus == "U") {
			rtnRowType = "update";
		} else if (rowStatus == "R") {
			rtnRowType = "normal";
		} else if (rowStatus == "D") {
			rtnRowType = "delete";
		}

		return rtnRowType;
	};

