			};

			return wprom.popupOpen("/ui/cbso/CBSO030P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();

console.log("====CBSO030P promise close \n"+JSON.stringify(rtnValue));	

			if (rtnValue) {
				id_etcMemo.setValue(rtnValue.etcMemo);
			}
		});
	};

