/**
   * @function	addrHistoryPopup
   * @desc		주소 History 오픈
   * @param      
   * 				str 
   * 	str  변수값 정의
   * 	arg1  = custNo
   * 	arg2  = seqNo
   * 	arg3  = sAdrsType
   * 	arg4  = DataSet
   * 	arg5  = row
   * 	arg6  = column sZip1
   * 	arg7  = column sZip2
   * 	arg8  = column sAdrs1
   * 	arg9  = column sAdrs2
   * 	arg10 = column sAdrs3
   * 	arg11 = column sAdrsAdd
   * 	arg12 = column sAdrsAdd2
   * 	arg13 = column sAdrs
   * 	arg14 = 포커스 이동할 컴포넌트
   **/
	scwin.addrHistoryPopup = function(str) {
		var pro = Promise.resolve();

		pro = pro.then(function(rv) {
			var indata  = {	"parentInfo" : str,
							"sHandlerID" : sHandlerID
						  };

			var popInfo = {
				"width"     : 732,
				"height"    : 320,  
				"indata"    : indata,
				"title"     : "주소 History 조회",
				"callback"  : scwin.popupCallback
			};

			return wprom.popupOpen("/ui/cbso/CBSO025P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();
console.log("====CBSO025P promise close \n"+JSON.stringify(rtnValue));
			if (rtnValue != "") {
				var args = str.split("^");

				var pDataSetName = wconv.trim(args[3]);
				var pDataSetRow  = wconv.trim(args[4]);

				if (wconv.length(pDataSetName) > 0 && wconv.length(pDataSetRow) > 0) {
					var colNm = "";
					for (var i=5;i<args.length;i++) {
						colNm = wconv.trim(args[i]);
						if (wconv.length(colNm) > 0) $w.data[pDataSetName].setCellData(0, colNm, rtnValue[colNm]);
					}
				}
			}
		});
	};

