	};

/**
   * @function	telHistoryPopup
   * @desc		연락처 History 오픈
   * @param      
   * 				str 
   * 	str  변수값 정의
   * 	arg1  = custNo
   * 	arg2  = seqNo
   * 	arg3  = DataSet
   * 	arg4  = row
   * 	arg5  = column 집전화1
   * 	arg6  = column 집전화2
   * 	arg7  = column 집전화3
   * 	arg8  = column 직장전화1
   * 	arg9  = column 직장전화2
   * 	arg10 = column 직장전화3
   * 	arg11 = column 휴대전화1
   * 	arg12 = column 휴대전화2
   * 	arg13 = column 휴대전화3
   * 	arg14 = column 기타1
   * 	arg15 = column 기타2
   * 	arg16 = column 기타3
   * 	arg17 = column 기타구분
   * 	arg18 = column Fax1
   * 	arg19 = column Fax2
   * 	arg20 = column Fax3
   * 	arg21 = column Eamil1
   * 	arg22 = column Eamil2
   * 	arg23 = 포커스 이동할 컴포넌트
   **/
	scwin.telHistoryPopup = function(str) {
		// 우편번호창 오픈
		//open("com_cut::OCU10200400s_P03.xml", "parentInfo="+str, 100, 100, true, 300, 100);
		//open("test::OCU10200400s_P03.xml", "parentInfo="+str, 100, 100, true, 300, 100);	
		// open("cst_cst::A085_Pop.xml", "parentInfo=" + str, 100, 100, true, 300, 100);
		var pro = Promise.resolve();

		pro = pro.then(function(rv) {
			var indata  = {	"parentInfo" : str,
							"sHandlerID" : sHandlerID
						  };

			var popInfo = {
				"width"     : 732,
				"height"    : 336,  
				"indata"    : indata,
				"title"     : "연락처 History 조회",
				"callback"  : scwin.popupCallback
			};

			return wprom.popupOpen("/ui/cbso/CBSO026P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();
console.log("====CBSO026P promise close \n"+JSON.stringify(rtnValue));
			if (rtnValue != "") {
				var args = str.split("^");

				var pDataSetName = wconv.trim(args[2]);
				var pDataSetRow  = wconv.trim(args[3]);

				if (wconv.length(pDataSetName) > 0 && wconv.length(pDataSetRow) > 0) {
					var colNm = "";
					for (var i=5;i<args.length;i++) {
						colNm = wconv.trim(args[i]);
						if (wconv.length(colNm) > 0) $w.data[pDataSetName].setCellData(0, colNm, rtnValue[colNm]);
					}
				}
			}
		});
	};

/**
 * @author      
 * @function	btn_search_OnClick 
 * @desc		직업정보 관리 조회 
 * @param      
 * 				arg 
 * 	arg  변수값 정의
 * 	flag  = button 에 대한 이름 설정 
 **/	
	scwin.bt_search_onclick = function() {
		var pro = Promise.resolve();

		pro = pro.then(function(rv) {
			var indata  = {	"flag" : "bt_search",
							"sStandDate" : sStandDate
						  };

			var popInfo = {
				"width"     : 845,
				"height"    : 565,  
				"indata"    : indata,
				"title"     : "직업코드 조회",
				"callback"  : scwin.popupCallback
			};

			//var arg = "flag=" + obj.ID;
			//arg = arg + " sStandDate=" + sStandDate; //2014.04.01 제도개정(직업코드)
			//var result = Dialog("cst_cin::A230_Pop.xml", arg, 800, 545);				
			return wprom.popupOpen("/ui/cbso/CBSO021P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();
console.log("====CBSO021P promise close \n"+JSON.stringify(rtnValue));
			if (rtnValue != "") {
				scwin.search_val(rtnValue);
			}
		});
	};

