/*
 * @author      
 * @function	btn_search_OnClick 
 * @desc		직업검색한 ds 셋팅.
*/
	scwin.search_val = function(result) {
		//if( rJobBtn == "bt_search" ) // flag

		var jobretArr = result.split("^");

		//trace("retArr"+jobretArr);

		/********* 추후 ...		20091117	****************************************************
		// 운전자 배서시 2009년 09월 이후 계약건 운전자 등급 변경시 직업변경 막음.
		if ( screenId == "common" )
		{
			if (parent.sToDate.Value > "20090930")
			{
				// 피보험자
				if (flag == "btn_Isd")
				{
					if (jobretArr[3] != parent.ds_ft30_0.GetColumn(0,"sDrvCls"))
					{
						alert("운전등급이 변경되어 직업 변경을 할수 없습니다. 확인해주십시요.");
						return;				
					}
				} 
				// 계약자
				else if (flag == "btn_gaeyakja")
				{
					if (jobretArr[3] != parent.ds_ft20.GetColumn(0,"sDrvCls"))
					{
