/**
 * @author      곽성실
 * @function	setSendAddr1
 * @desc		발송지 주소 세팅
 * @param      
 * 				dsStr : DataSet 명
 **/
	scwin.setSendAddr1 = function(code) {
	    var dsStr; // 주소 DataSet 명
	    var addrStr; // 주소 구분 명
	    //if (code == 0) {
	    // 발송지 주소 초기화
	    //dataTab.tab1.Mdt_SendZip.Value = "";
	    //dataTab.tab1.Mdt_SendAddr.Text = "";
	    //} else {
	    // 해당 주소 DataSet
	    if (code == "1") {
	        dsStr = "Ds_AddrHome";
	    } else if (code == "2") {
	        dsStr = "Ds_AddrOffice";
	    } else if (code == "3") {
	        dsStr = "Ds_AddrEtc";
	    } else if (code == "4") {
	        dsStr = "Ds_AddrTemp";
	    }
	    if (Cmb_SendType.getValue() == code) {
	        addrStr = Ds_SendType.getCellData(code, "sCodeName");
	        if (wconv.length($w.data[dsStr].getCellData(0, "sZip1")) < 1 || wconv.length($w.data[dsStr].getCellData(0, "sZip2")) < 1 || wconv.length($w.data[dsStr].getCellData(0, "sAdrs")) < 1 || wconv.length($w.data[dsStr].getCellData(0, "sAdrsAdd")) < 1) {} else {
	            Mdt_SendZip.setValue($w.data[dsStr].getCellData(0, "sZip1") + $w.data[dsStr].getCellData(0, "sZip2"));
	            Mdt_SendAddr.setValue($w.data[dsStr].getCellData(0, "sAdrs") + " " + $w.data[dsStr].getCellData(0, "sAdrsAdd")); //
	        }
	    }
	};
	scwin.btn_Confidence_OnClick = function() {
		if (wconv.length(Edt_Name.getValue()) <= 0) {
			Edt_Name.focus();
			alert("고객명을 입력하세요.");
			return;
		}

		var indata = {
				"custId" : custId,
				"custNo" : Edt_CustNo.getValue(),
				"custName" : Edt_Name.getValue(),
				"handlerID" : sHandlerID 
			} 

		var pro = Promise.resolve();

		pro = pro.then(function(rv) {
			var popInfo = {
				"width"     : 468,
				"height"    : 315,  
				"indata"    : indata,
				"title"     : "가입설계동의"
			};

			return wprom.popupOpen("/ui/cbsc/CBSC063P.xml", popInfo);
		});	
	};
