var G_POPUP_DATE = "";

//---------------------------------------------------------------------------
// function 명 : frmCalendarMonth_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용 
//---------------------------------------------------------------------------
scwin.init = function() {
    var sToday = wconv.today();
    var nowYear = wconv.left(sToday, 4);
    if (G_POPUP_DATE != "") {
        lbl_Year.setValue(wconv.left(G_POPUP_DATE, 4));
    } else {
        lbl_Year.setValue(nowYear);
    }
Button2.hide();
};

//---------------------------------------------------------------------------
// function 명 : img_Close_OnClick
// 내용        : 닫기버튼 Event처리
//---------------------------------------------------------------------------
scwin.img_Close_OnClick = function(obj, nX, nY) {
    if (this.id == "PopDiv_Calendar")
        this.hidden("");
    else
        wprom.close("");
};

//---------------------------------------------------------------------------
// function 명 : common_OnClick
// 내용        : 확인버튼 Event처리
//---------------------------------------------------------------------------
scwin.common_OnClick = function(e){
    var strValue = wconv.trim(this.getValue());
    if (wconv.length(strValue) == 1) {
        strValue = "0" + strValue;
    } else {
        strValue = strValue;
    }
    //if (this.id == "PopDiv_Calendar")
    //    this.hidden(lbl_Year.getValue() + "-" + strValue);
    //else
console.log("★★=================="+lbl_Year.getValue() + strValue);
    wprom.close(lbl_Year.getValue() + strValue);
};

//---------------------------------------------------------------------------
// function 명 : img_Left_OnClick
// 내용        : 년도감소 Event처리
//---------------------------------------------------------------------------
scwin.img_Left_OnClick = function(obj, nX, nY) {
    lbl_Year.setValue(eval(lbl_Year.getValue()) - 1);
};

//---------------------------------------------------------------------------
// function 명 : img_Right_OnClick
// 내용        : 년도증감 Event처리
//---------------------------------------------------------------------------
scwin.img_Right_OnClick = function(obj, nX, nY) {
    lbl_Year.setValue(eval(lbl_Year.getValue()) + 1);
};
