/**
 * @function	zipPopup
 * @desc		 
 * @param      
 * 				str 
 * 	str   
 * 	
 * 	arg1^arg2^arg3^arg4^arg5^arg6^arg7^arg8^arg9^arg10
 * 	arg1 = DataSet
 * 	arg2 = row
 * 	arg3 = column 1
 * 	arg4 = column 2
 * 	arg5 = column addr1
 * 	arg6 = column addr2
 * 	arg7 = column 
 * 	arg8 = column 
 * 	arg9 = column 
 * 	arg10 =    
 **/
scwin.zipPopup = function(str) {

    //trace("/"+str);
    //  
    //open("com_cod::OCP10010100s_P01.xml", "parentInfo="+trim(str), 100, 100, true, 300, 100);
    //open("cst_cin::A220_Pop.xml", "parentInfo=" + wconv.trim(str), 100, 100, true, 300, 100);
    var promise = Promise.resolve();
    var pobj = $w.data[str.split("^")[0]];
    promise = promise.then(function(){
	    var indata = {"parentInfo":str};
	    var parentObj = {
	    	"Ds_Addr" : pobj
	    };
	    var popInfo = {
	    		width : 729,
	    		height : 616,
	    		left:100,
	    		top : 100,
	    		title : " ",
	    		"indata":indata,
	    		"parentObject":parentObj
	    	};
		//wprom.windowOpen("/ui/cbso/CBSO031P.xml",popInfo);
		wprom.popupOpen("/ui/cbso/CBSO031P.xml",popInfo);
	});
	promise = promise.then(function(){
		pobj.setRowPosition(0);
		return;
	});
	return promise;
	//wprom.popupOpen("/ui/cbso/CBSO031P.xml",popInfo);
    //open("cst_cin::A220_Pop.xml", "parentInfo=" + wconv.trim(str), 100, 100, true, 300, 100);
};

