scwin.Btn_Save_OnClick = function(e) {
    try{
    	str_ScanObjYn = parentWin.Ds_LTIDA00.getCellData(0, "sScanObjtYn");
    }catch(e){
    	str_ScanObjYn = "";
    };
    if (wconv.length(me_sMainCustNo.getValue()) > 12) {
        //    
        if (scwin.lfn_getCUSAA42Info(me_sMainCustNo.getValue()) == "Y") {
            if (str_ScanObjYn == "Y") {
                wprom.alert("    .");
            } else {
                wprom.alert("    .");
                //btn_InsDesign.Color = "user40";
                //btn_InsDesign.Font = ",9,Bold";
            }
            return;
        } else {
            //btn_InsDesign.Color = "user41";
            //btn_InsDesign.Font = ",9";
        }


        //     
        if (sImsiYN == "N" && sGoodAgreeFlag == "N") {
            //Y  skip
            if (scwin.lfn_getCUSAA0441Info(me_sMainCustNo.getValue()) == "Y") {
                if (str_ScanObjYn == "Y") {
                    wprom.alert("    .");
                } else {
                    wprom.alert("    .");
                    //btn_GoodsIntro.Color = "user40";
                    //btn_GoodsIntro.Font = ",9,Bold";
                }
                return;
            } else {
                //btn_GoodsIntro.Color = "user41";
                //btn_GoodsIntro.Font = ",9";
            }
        }
    }
    scwin.userInfoSave(); //Tab_Req.Tab_Step1.Div_Step1.CallScript("Button3_OnClick(obj)");
};

/**
