    //parentWin.eval(sDataset).copy(ds_long_mPlr);
	//parentWin.eval(sDataset).setJSON(ds_long_mPlr.getAllJSON(),true);
	parentWin.Ds_long_mPlr.setJSON(ds_long_mPlr.getAllJSON());

    wprom.close(tempAdrFlag); /*-----------------------------------------------------------------------------------------------*/
};

