scwin.btn_search_OnClick = function(obj) {
    var promise = Promise.resolve();
    var arg = "flag=" + obj.ID;
    promise = promise.then(function(rtnVal) {
        return wprom.promise.popupOpen("PROJECT_PATH_TEMP/com/fCom01050s.xml", arg, 800, 545);
    });
    promise = promise.then(function(result) {
        /*wprom.popupOpen()*/
        //Edt_sJobCode.setDisabled(true); // 
        //Edt_sJobNam.setDisabled(true); //  
        //        , 
        //  20080829
        ////////////////////////////////	
        if (result != -1)
            scwin.search_val(); /////////////////////////////////		
    });
    return promise;
};

