    Ds_UserInfo.setCellData(0, "sJobClass", wconv.trim(rJobCls)); // ) ds  
    //  Edt_sJobCode( ) 01      
    if (wconv.substr(Edt_sJobCode.getValue(), 0, 2) == "01") {
        var jobargs = Edt_sJobCode.getValue() + "^" + Edt_sJobNam.getValue();
        promise = promise.then(function(rtnVal) {
