            return wprom.promise.popupOpen("PROJECT_PATH_TEMP/cut/fCut01015s.xml", "jobargs=" + jobargs /*wprom.popupOpen()*/ , 100, 100, true, 400, 200);
        });
        promise = promise.then(function(reVal) {
            var retArr = wconv.split(reVal, "^");
