                //dataTab.tab1.Edt_2_AdrsAdd.Text	= trim(retArr[7])+" "+trim(retArr[1]) ;	
                //Ds_AddrOffice.setCellData(0, "cType","insert");
                Ds_AddrOffice.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
                Ds_AddrOffice.setCellData(0, "sZip1", wconv.trim(retArr[2]));
                Ds_AddrOffice.setCellData(0, "sZip2", wconv.trim(retArr[3]));
                Ds_AddrOffice.setCellData(0, "sAdrs1", wconv.trim(retArr[4]));
                Ds_AddrOffice.setCellData(0, "sAdrs2", wconv.trim(retArr[5]));
                Ds_AddrOffice.setCellData(0, "sAdrs3", wconv.trim(retArr[6]));
                Ds_AddrOffice.setCellData(0, "sAdrsAdd", dataTab_tab1_Edt_2_AdrsAdd.getValue());

                //Ds_UserInfo.setCellData(0, "sJobGroup", trim(substr(rOldJobCod,0,2)));	// ) ds  
