/**
 * @author      
 * @function	setSendAddr1
 * @desc		  
 * @param      
 * 				dsStr : DataSet 
 **/
scwin.setSendAddr1 = function(code) {
    var dsStr; //  DataSet 
    var addrStr; //   
    //if (code == 0) {
    //   
    //dataTab.tab1.Mdt_SendZip.Value = "";
    //dataTab.tab1.Mdt_SendAddr.Text = "";
    //} else {
    //   DataSet
    if (code == "1") {
        dsStr = "Ds_AddrHome";
    } else if (code == "2") {
        dsStr = "Ds_AddrOffice";
    } else if (code == "3") {
        dsStr = "Ds_AddrEtc";
    } else if (code == "4") {
        dsStr = "Ds_AddrTemp";
    }
    if (dataTab_tab1_Cmb_SendType.getValue() == code) {
        addrStr = Ds_SendType.getCellData(code, "sCodeName");
        if (wconv.length(eval(dsStr).getCellData(0, "sZip1")) < 1 || wconv.length(eval(dsStr).getCellData(0, "sZip2")) < 1 || wconv.length(eval(dsStr).getCellData(0, "sAdrs")) < 1 || wconv.length(eval(dsStr).getCellData(0, "sAdrsAdd")) < 1) {} else {
            dataTab_tab1_Mdt_SendZip.setValue(  eval(dsStr).getCellData(0, "sZip1") + eval(dsStr).getCellData(0, "sZip2") );
            dataTab_tab1_Mdt_SendAddr.setValue(  eval(dsStr).getCellData(0, "sAdrs") + " " + eval(dsStr).getCellData(0, "sAdrsAdd") ); //
        }
    }
};

scwin.check_telno = function(telno1) {
    if ((telno1 != "02" && telno1 != "032" && telno1 != "031" && telno1 != "033" && 
         telno1 != "043" && telno1 != "042" && telno1 != "063" && telno1 != "053" && 
         telno1 != "052" && telno1 != "051" && telno1 != "055" && telno1 != "061" && 
         telno1 != "062" && telno1 != "064") && wconv.length(telno1) > 0) {
        wprom.alert(" ");
        return ;
    }
};

scwin.check_cellphone = function(telno1) {
    if ((telno1 != "016" && telno1 != "017" && telno1 != "018" && telno1 != "019" && telno1 != "011" && telno1 != "010") && wconv.length(telno1) < 0) {
        wprom.alert("  ");
        return ;
    }
};

scwin.dataTab_tab1_Edt_HomeTel1_OnFocus = function(e) {
    scwin.check_telno(wconv.trim(dataTab_tab1_Edt_HomeTel1.getValue()));
};

scwin.dataTab_tab1_Edt_OfficeTel1_OnKillFocus = function(e) {
    scwin.check_telno(wconv.trim(dataTab_tab1_Edt_OfficeTel1.getValue()));
};

scwin.dataTab_tab1_Edt_Fax1_OnKillFocus = function(e) {
    scwin.check_telno(wconv.trim(dataTab_tab1_Edt_Fax1.getValue()));
};

scwin.dataTab_tab1_Edt_CellPhone1_OnKillFocus = function(e) {
    scwin.check_cellphone(wconv.trim(dataTab_tab1_Edt_CellPhone1.getValue()));
};

scwin.dataTab_tab1_Edt_EtcTel1_OnKillFocus = function(e) {
    var telno1 = wconv.trim(dataTab_tab1_Edt_EtcTel1.getValue());
    if ((telno1 != "02" && telno1 != "032" && telno1 != "031" && telno1 != "033" && telno1 != "043" && telno1 != "042" && telno1 != "063" && telno1 != "053" && telno1 != "052" && telno1 != "051" && telno1 != "055" && telno1 != "061" && telno1 != "062" && telno1 != "064" && telno1 != "016" && telno1 != "017" && telno1 != "018" && telno1 != "019" && telno1 != "011" && telno1 != "010") && wconv.length(telno1) > 0) {
        wprom.alert("() ");
        return false;
    }
};

