/*
parentInfo 변수값 정의

arg1^arg2
arg1 = custNo
arg2 = seqNo
arg3 = sType
arg4 = sAddrType
arg5 = nAddrSeq
arg6 = nTelSeq
arg7 = sSendType
arg8 = nSendSeq
*/
var parentInfo;
var args;
var getSysDate;
var editBln;//등록 ,수정 작업 
var custId;//고객 ID 
var custNo;//고객 번호 
var seqNo;//순번 
var sType;//  구분 (개인, 법인,단체 ) 에 따라 스크립트 달라짐 
var sAddrType;//피보험자 구분 
var nAddrSeq;// 피보험자 회차  
var nTelSeq;// 전화 회차 
var sSendType;// 발송지 주소 구분 
var nSendSeq;// 발송지 회차 
