var sHandlerID = "";	//취급자사번
// 화면 ID : "common" 은 공통으로 사용하는 경우
// 전달이 없는 경우는 null (운전자보험 청약메인:fReq01010s.xml)
/*
- [고객정보] 전달 관련
- ds_tmp_mPlr : 운전자보험 청약에서의 계약자 테이블[FTREQ020]에 준하여 정의.
  공통 임시 데이타셋으로서 부모창에서는 임시 데이타셋으로 전달받고 부모창에서 사용.
  flag :  값 추가  : 2008-08-14 일 목요일 
  다음 형식으로 호출.
  Dialog("cut::fCut01010s.xml", "parentInfo="+ed_cont_cd.Value+"^0 screenId=common" , 820, 612);
*/
var  fv_args;
var parentWin;
var str_ScanObjYn;

scwin.init = function() {
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);//parentInfo,sTableName,sAdrsType
	} catch(e) {}
	//부모창 
	//Ds_long_mPlr 
	//Ds_LTIDA00
	parentWin = wprom.getParamObject();

    //현재시간 가져오기
    gfn_getServerTime();
    getSysDate = gds.gDs_ServerTime.getCellData(0, "sToday");

    //서버시간으로 pc시간 동기화 처리
    //goo ext_SetTime(getSysDate);
	    if (wconv.length(fv_args.sHandlerID) > 0) {
	        sHandlerID = fv_args.sHandlerID;
	    }

    // 파라미터로 넘어온 값을 split
    //args = "^1262296630^0^3^1^2^2^1^1^Ds_long_mPlr".split( "^" );
	args = fv_args.parentInfo.split( "^" );
    //alert("파라미터 값 ::::::::::::::: =>>>>>>>"+wconv.length(args)+":"+args[1]+":"+args[2]);
    //trace("파라미터 값 :::::::::::::::"+screenId);
    //trace("파라미터 값 :::::::::::::::"+flag);
console.log("parentInfo : ["+args+"]");    
    if (wconv.length(args) > 1  &&    wconv.length(wconv.trim(args[1])) > 0 &&  wconv.length(wconv.trim(args[2])) > 0) {
        custId = wconv.trim(args[0]); // 고객 ID
        custNo = wconv.trim(args[1]); // 주민번호 
        seqNo = wconv.trim(args[2]); // seq 
        sType = wconv.trim(args[3]); // 개인,법인,단체 
        sAddrType = wconv.trim(args[4]); // 피보험자 구분 
        nAddrSeq = wconv.trim(args[5]); // 피보험자 회차  
        nTelSeq = wconv.trim(args[6]); // 전화 회차
        sSendType = wconv.trim(args[7]); // 발송지 주소 구분 
        nSendSeq = wconv.trim(args[8]); // 발송지 회차 
        sDataset = wconv.trim(args[9]); // 데이터 셋 명 
        // 활성화구분값 :' '  : 전체 활성화 ,  '
        //  1' : 주소및 연락처 PART 활성화, 법인 고객등록 POPUP화면의 상단부는 비 활성화.
        // '2' : 직업코드만 활성화
        // '3' : 계약변경시 사용 (발송지 체크 안함)
        // '4' : 주소 , 연락처 , 직업코드 활성화 
        EnableType = wconv.trim(args[10]);

        // 조회 
        scwin.search(custNo, seqNo, sAddrType, nAddrSeq, nTelSeq, sSendType, nSendSeq);
    }else {
        wprom.alert("검색 조건이 없습니다. 확인후 다시 시도해 주세요.");
        return false;
    }
    if (sType == "3") {
        me_sMainCustNo.setDisabled(true);
        me_sCorpNo.setDisabled(true);
    } else {
        me_sMainCustNo.setDisabled(false);
        me_sCorpNo.setDisabled(false);
    }
//console.log("★★★★★★★ 1111");
    // 대면 스캔인 경우 임시 버튼. 가입설계 동의버튼, 상품소개 버튼 사용안함.
    try{
    	str_ScanObjYn = parentWin.Ds_LTIDA00.getCellData(0, "sScanObjtYn");
    }catch(e){
    	str_ScanObjYn = "";
    };
    if ((str_ScanObjYn == "Y") || (util.getGdsValue("gFaceSalesYN") == "Y")) {
        btn_GoodsIntro.hide();
        btn_InsDesign.hide();
        sImsiYN.hide();
        sImsiYN = "Y"; //대면은 상품소개 동의 체크 뒤에서. 
    }



    // 계약자 인적사항 수정시 전체 Enable (계약자는 보험료와 무관함.)  , 
    // 2,3일때는 계약변경에서 발송지체크 안하도록 한다.
    if (EnableType == "1" || EnableType == "4") {
        scwin.eventEnable( wconv.trim(EnableType));
    }
    Edt_CustNo.focus();
};

