/**
 * @function	search
 * @desc		고객정보, 주소, 연락처 검색
 * @param		custNo : 주민등록번호/사업자 번호
 * search(custNo, seqNo,nPlrAddrSeq,nPlrTelSeq,nPlrDMAddrSeq);
 **/
scwin.search = function(custNo, seqNo, sAddrType, nAddrSeq, nTelSeq, sSendType, nSendSeq) {
    // Dataset 초기화
    wconv.clear(DsWs_UserInfo);
    wconv.clear(DsWs_TelInfo);
    wconv.clear(DsWs_TelInfo_1);
    wconv.clear(DsWs_AddrHome);
    //wconv.clear(Ds_AddrHome);
    wconv.clear(DsWs_AddrHome_1);
    wconv.clear(DsWs_AddrOffice);
    wconv.clear(Ds_AddrOffice);
    wconv.clear(DsWs_AddrEtc);
    wconv.clear(Ds_AddrEtc);
    wconv.clear(DsWs_AddrTemp);
    //wconv.clear(Ds_AddrTemp);
    wconv.clear(DsWs_CUSBA01);
    wconv.clear(DsWs_DMAddr);

    wconv.clear(DsWs_CUSAA20);
    wconv.clear(Ds_CUSAA20);//.removeAll();
	/*orign
    //	SOBJ_control.WSDL = gWsdlCustomer;	// 서비스 Obj WSDC 설정
    SOBJ_control.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정 
    //var MethodObj = gfn_CreateMethodObj(SOBJ_control, "com.inswave.newgen.com.cut.task.OCU10200400Task", "getCustomerInfo", 0, 0);
    var MethodObj = gfn_CreateMethodObj(SOBJ_control, "com.inswave.longterm.cst.cst.task.CAA075Task", "getCustomerInfo", 0, 0);

    // 주민등록번호/사업자번호 세팅
    gfn_SetParam(MethodObj, "sCustNo", custNo);
    // 고객, 연락처, 주소 순번 세팅
    gfn_SetParam(MethodObj, "nSeqNoCust", seqNo);
    gfn_SetParam(MethodObj, "nSeqNoTel", "0");
    gfn_SetParam(MethodObj, "nSeqNoAddr", "0");
    gfn_SetParam(MethodObj, "Cmb_SendType", sSendType);
    gfn_SetParam(MethodObj, "nPlrAddrSeq", nAddrSeq);
    gfn_SetParam(MethodObj, "nPlrTelSeq", nTelSeq);
    gfn_SetParam(MethodObj, "nPlrDMAddrSeq", nSendSeq);
    gfn_CallService(SOBJ_control, DOBJ_control, MethodObj);
	*/

console.log("★★★★★★custNo :" + custNo+"^"+seqNo+"^"+sSendType+"^"+nAddrSeq+"^"+nTelSeq+"^"+nSendSeq);	
	var MethodObj = new callService("com.inswave.longterm.cst.cst.task.CAA075Task", "getCustomerInfo", 0, 0);
	MethodObj.setId("getCustomerInfo");
	// 주민등록번호/사업자번호 세팅
    MethodObj.setParam( "sCustNo", custNo);
    // 고객, 연락처, 주소 순번 세팅
    MethodObj.setParam( "nSeqNoCust", seqNo);
    MethodObj.setParam( "nSeqNoTel", "0");
    MethodObj.setParam( "nSeqNoAddr", "0");
    MethodObj.setParam( "Cmb_SendType", sSendType);
    MethodObj.setParam( "nPlrAddrSeq", nAddrSeq);
    MethodObj.setParam( "nPlrTelSeq", nTelSeq);
    MethodObj.setParam( "nPlrDMAddrSeq", nSendSeq);

	MethodObj.setUrl("/common/CBSO012PController.do");
	MethodObj.setXPath("//*/hashtable/data/CUSAA01");
	MethodObj.addXPath("//*/hashtable/data/CUSAA03");
	MethodObj.addXPath("//*/hashtable/data/SelectCUSAA03");	
	MethodObj.addXPath("//*/hashtable/data/AddrHomeInfo");
	MethodObj.addXPath("//*/hashtable/data/SelectCUSAA02");
	MethodObj.addXPath("//*/hashtable/data/AddrOfficeInfo");
	MethodObj.addXPath("//*/hashtable/data/AddrEtcInfo");
	MethodObj.addXPath("//*/hashtable/data/AddrTempInfo");
	MethodObj.addXPath("//*/hashtable/data/selectCUSBA01");
	MethodObj.addXPath("//*/hashtable/data/CUSAA02_DM");
	MethodObj.addXPath("//*/hashtable/data/CUSAA20");
	MethodObj.addXPath("//*/hashtable/data/vector/data/commSend");
	MethodObj.addXPath("//*/hashtable/data/vector/data/commTelEtc");	

	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA01"), "DsWs_UserInfo");
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA03"), "DsWs_TelInfo");
	util.jSonDsCreate(_tranSync.getDsJson("SelectCUSAA03"), "DsWs_TelInfo_1");

	util.jSonDsCreate(_tranSync.getDsJson("AddrHomeInfo"), "DsWs_AddrHome");
	util.jSonDsCreate(_tranSync.getDsJson("SelectCUSAA02"), "DsWs_AddrHome_1");

	util.jSonDsCreate(_tranSync.getDsJson("AddrOfficeInfo"), "DsWs_AddrOffice");
	Ds_AddrOffice.setJSON (_tranSync.getDsJson("AddrOfficeInfo"));

	util.jSonDsCreate(_tranSync.getDsJson("AddrEtcInfo"), "DsWs_AddrEtc");
	Ds_AddrEtc.setJSON (_tranSync.getDsJson("AddrEtcInfo"));  

	util.jSonDsCreate(_tranSync.getDsJson("AddrTempInfo"), "DsWs_AddrTemp");
	Ds_AddrTemp.setJSON (_tranSync.getDsJson("AddrTempInfo"));

	util.jSonDsCreate(_tranSync.getDsJson("selectCUSBA01"), "DsWs_CUSBA01");
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA02_DM"), "DsWs_DMAddr");

	util.jSonDsCreate(_tranSync.getDsJson("CUSAA20"), "DsWs_CUSAA20");
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA20"), "Ds_CUSAA20");
	util.jSonDsCreate(_tranSync.getDsJson("commSend"), "Ds_SendType");
	//util.setCompBindRefresh("Ds_SendType");
	//util.setRowPosition(Ds_SendType,0);
	//이메일
	gds.gDs_Email_Create();
	dataTab_tab1_Cmb_Email1.setNodeSet("data:gDs_Email","TEXT","CODE");
	//
	util.jSonDsCreate(_tranSync.getDsJson("commTelEtc"), "Ds_TellEtcType");
	util.setCompBindRefresh("Ds_TellEtcType");
	util.setRowPosition(Ds_TellEtcType,0);

    //  신용정보 조회 
    if (DsWs_CUSBA01.getRowCount() < 1 || DsWs_CUSBA01.getCellData(0, "result") == "0") {
        util.appendRow(Ds_CUSBA01);
    } else {
        //wconv.copy(Ds_CUSBA01, "DsWs_CUSBA01");
        util.jSonDsCreate(DsWs_CUSBA01.getAllJSON(), "Ds_CUSBA01");
		util.setCompBindRefresh("Ds_CUSBA01");
		util.setRowPosition(Ds_CUSBA01,0);

    }


    //사용자 정보 
    if (DsWs_UserInfo.getRowCount() < 1 || DsWs_UserInfo.getCellData(0, "result") == "0") {
        util.appendRow(Ds_UserInfo);
        //Ds_UserInfo.insertRow( 0 );

        Ds_UserInfo.setCellData(0, "sCustNo", custNo);
        Ds_UserInfo.setCellData(0, "nSeqNo", seqNo);
    } else {
        //wconv.copy(Ds_UserInfo, "DsWs_UserInfo");
        wconv.clear(Ds_UserInfo);
        util.jSonDsCreate(DsWs_UserInfo.getAllJSON(), "Ds_UserInfo");
        util.setCompBindRefresh("Ds_UserInfo");
		util.setRowPosition(Ds_UserInfo, 0);        
        //최초등록자		
        gfn_selectEmpNameByAcct(Ds_UserInfo.getCellData(0, "sCreateID"));
