/**
 * @function	zipPopup
 * @desc		주소찾기창을 띄움
 * @param      
 * 				str 
 * 	str  변수값 정의
 * 	
 * 	arg1^arg2^arg3^arg4^arg5^arg6^arg7^arg8^arg9^arg10
 * 	arg1 = DataSet
 * 	arg2 = row
 * 	arg3 = column 우편번호1
 * 	arg4 = column 우편번호2
 * 	arg5 = column 우편번호addr1
 * 	arg6 = column 우편번호addr2
 * 	arg7 = column 시도
 * 	arg8 = column 시군구
 * 	arg9 = column 읍면동
 * 	arg10 = 포커스 이동할 컴포넌트 
 **/
scwin.zipPopup = function(str) {

    //trace("우편번호/테스트"+str);
    // 우편번호창 오픈
    //open("com_cod::OCP10010100s_P01.xml", "parentInfo="+trim(str), 100, 100, true, 300, 100);
    //open("cst_cin::A220_Pop.xml", "parentInfo=" + wconv.trim(str), 100, 100, true, 300, 100);
    var promise = Promise.resolve();
    var pobj = $w.data[str.split("^")[0]];
    promise = promise.then(function(){
	    var indata = {"parentInfo":str};
	    var parentObj = {
	    	"Ds_Addr" : pobj
	    };
	    var popInfo = {
	    		width : 729,
	    		height : 616,
	    		left:100,
	    		top : 100,
	    		title : "우편번호 찾기",
	    		"indata":indata,
	    		"parentObject":parentObj
	    	};
		//wprom.windowOpen("/ui/cbso/CBSO031P.xml",popInfo);
		wprom.popupOpen("/ui/cbso/CBSO031P.xml",popInfo);
	});
	promise = promise.then(function(){
		pobj.setRowPosition(0);
		return;
	});
	return promise;
	//wprom.popupOpen("/ui/cbso/CBSO031P.xml",popInfo);
    //open("cst_cin::A220_Pop.xml", "parentInfo=" + wconv.trim(str), 100, 100, true, 300, 100);
};

/**
 * @author           
 * @function	Btn_Save_OnClick
 * @desc		
 *              고객정보, 주소정보, 연락처정보 저장 버튼 클릭 작업 
 **/
scwin.Btn_Save_OnClick_async = function(e) {
	util.eventAsyncCall(this, scwin.Btn_Save_OnClick, arguments); 
};

scwin.Btn_Save_OnClick = function(e) {
    try{
    	str_ScanObjYn = parentWin.Ds_LTIDA00.getCellData(0, "sScanObjtYn");
    }catch(e){
    	str_ScanObjYn = "";
    };
    if (wconv.length(me_sMainCustNo.getValue()) > 12) {
        // 가입설계 동의 여부 체크
        if (scwin.lfn_getCUSAA42Info(me_sMainCustNo.getValue()) == "Y") {
            if (str_ScanObjYn == "Y") {
                wprom.alert("대표자의 가입설계 동의서 스캔을 확인하세요.");
            } else {
                wprom.alert("대표자의 가입설계 동의 여부를 입력하세요.");
                //btn_InsDesign.Color = "user40";
                //btn_InsDesign.Font = "굴림,9,Bold";
            }
            return;
        } else {
            //btn_InsDesign.Color = "user41";
            //btn_InsDesign.Font = "굴림,9";
        }


        // 상품소개 동의 여부 체크 
        if (sImsiYN == "N" && sGoodAgreeFlag == "N") {
            //Y 인경우는 skip
            if (scwin.lfn_getCUSAA0441Info(me_sMainCustNo.getValue()) == "Y") {
                if (str_ScanObjYn == "Y") {
                    wprom.alert("대표자의 상품소개 동의서 스캔을 확인하세요.");
                } else {
                    wprom.alert("대표자의 상품소개 동의 여부를 입력하세요.");
                    //btn_GoodsIntro.Color = "user40";
                    //btn_GoodsIntro.Font = "굴림,9,Bold";
                }
                return;
            } else {
                //btn_GoodsIntro.Color = "user41";
                //btn_GoodsIntro.Font = "굴림,9";
            }
        }
    }
    scwin.userInfoSave(); //Tab_Req.Tab_Step1.Div_Step1.CallScript("Button3_OnClick(obj)");
};

/**
 * @function	userInfoSave
 * @desc		입력값 체크
 *              고객정보, 주소정보, 연락처정보 저장
 * @return		true/false
 **/
scwin.userInfoSave = function() {

    editBln = "false"; // 고객 정보 (기본, 주소, 연락처)를 입력/수정 여부 확인
    /* 입력값 체크 시작 */
    //check_telno(trim(dataTab.tab1.Edt_HomeTel1.text));
    //check_telno(trim(dataTab.tab1.Edt_OfficeTel1.text));
    //check_cellphone(trim(dataTab.tab1.Edt_CellPhone1.text));
    if (wconv.length(wconv.trim(Edt_CustNo.getValue())) == "") {
        wprom.alert("사업자번호는 필수입력 입니다.");
        Edt_CustNo.focus();
        return false;
    }


    //  계약자 구분 1  , 계약자 코드 10자리 이면 주민번호 필수 
    if (sType == "1" && wconv.length(wconv.trim(Edt_CustNo.getValue())) == "10") {
        if (wconv.trim(me_sMainCustNo.getValue()) == "" || wconv.trim(me_sMainCustNo.getValue()) == null) {
            wprom.alert("주민등록번호는 필수입력 입니다.");
            me_sMainCustNo.focus();
            return false;
        }
    }



    //주민번호 체크
    if (wconv.length(wconv.trim(me_sMainCustNo.getValue())) > 0) {
        if (!checkPersonIDSimple(wconv.trim(me_sMainCustNo.getValue()))) {
            wprom.alert("주민등록번호가 형식에 맞지않습니다.");
            me_sMainCustNo.focus();
            return false;
        }
    }



    //  계약자 구분 2 , 계약자 코드 10자리 이면 주민번호 필수 
    /*if ( sType =="2" && length(trim(Edt_CustNo.Value))=="10" ) {
    	if(trim(me_sCorpNo.Value) == "" || trim(me_sCorpNo.Value) == null)
    	alert("법인번호는 필수입력 입니다.");
    	me_sCorpNo.SetFocus();
    	return false;
    }*/
    // 고객명 체크
    if (wconv.length(wconv.trim(Edt_Name.getValue())) < 2) {
