// SteType 변경에 따른 처리 
scwin.dataTab_tab1_Cmb_SendType_OnChanged = function(info) {
    scwin.setSendAddr(info.newValue); //alert("발송지 = "+dataTab.tab1.Cmb_SendType.getValue());
};

/**
 * @author      곽성실
 * @function	setSendAddr
 * @desc		발송지 주소 세팅
 * @param      
 * 				dsStr : DataSet 명
 *  code 1:  자택 주소 :, 2: 직장주소,  3: 기타 주소 , 4: 임시주소 
 **/
scwin.setSendAddr = function(code) {
    var dsStr; // 주소 DataSet 명
    var addrStr; // 주소 구분 명
    //if (code == 0) {
    // 발송지 주소 초기화
    dataTab_tab1_Mdt_SendZip.setValue( "" );
    dataTab_tab1_Mdt_SendAddr.setValue( "" );
    //} else {
    // 해당 주소 DataSet
    if (code == "1") {
        // 자택 주소 
        dsStr = "Ds_AddrHome";
    } else if (code == "2") {
        // 직장주소 
        dsStr = "Ds_AddrOffice";
    } else if (code == "3") {
        // 기타 주소 
        dsStr = "Ds_AddrEtc";
    } else {
        dsStr = "Ds_AddrTemp"; // 임시주소 
    }
    addrStr = Ds_SendType.getCellData(code, "sCodeName");
    if (wconv.length(eval(dsStr).getCellData(0, "sZip1")) < 1 || wconv.length(eval(dsStr).getCellData(0, "sZip2")) < 1 || 
        wconv.length(eval(dsStr).getCellData(0, "sAdrs")) < 1 || wconv.length(eval(dsStr).getCellData(0, "sAdrsAdd")) < 1) {
        wprom.alert("먼저 " + addrStr + "주소를 입력해 주세요.");
        //Cmb_SendType.SetFocus();
        dataTab_tab1_Cmb_SendType.setValue( "0" );
    } else {
        dataTab_tab1_Mdt_SendZip.setValue(  eval(dsStr).getCellData(0, "sZip1") + eval(dsStr).getCellData(0, "sZip2") );
        dataTab_tab1_Mdt_SendAddr.setValue(  eval(dsStr).getCellData(0, "sAdrs") + " " + eval(dsStr).getCellData(0, "sAdrsAdd")); //nIsdDMAddrSeq  = Eval(dsStr).getCellData(0, "nIsdDMAddrSeq"); //발송회차 
    }
    editBln = "true"; //}
};

