 */
 //사용안함. 20171030 구종식
scwin.search_val = function() {
    var promise = Promise.resolve();
    if (rJobBtn == "bt_search") // flag
    {
        Edt_sJobCode.setValue( rJobCod); //직업코드 
        Edt_sJobNam.setValue( rJobNam); //직업명 
        Edt_sJobCode.setValue(rJobCod ); //직업코드 
        Edt_sJobNam.setValue( rJobNam); //직업명 
        //rJobBtn = flag;			// flag
        //rJobCod = str_jobcod;	// 직업코드 
        //rJobCls = str_jobrsk; 	// 직업등급 
        //rJobNam = str_jobname; 	// 운전등급 
        //rDrvCls = sDriveClass;	// 직업코드 
        //rDrvCls = sDriveClass;	// 직업코드 
        //  데이터 저장 
        //Ds_UserInfo.setCellData(0, "sSchoolCode",trim(retArr[0]));	// 직업정보 코드 
        //Ds_UserInfo.setCellData(0, "sSchoolName",trim(retArr[1]));	// 직업정보 
    }














    // 고객 정보 dataset 에 작업 
    // 직업 및  데이터 넣어주기 
    Ds_UserInfo.setCellData(0, "sJobGroup", wconv.trim(wconv.substr(rOldJobCod, 0, 2))); // 본인)직업코드 ds에 등록 
    // 2008.09.03 유영민 변경
    Ds_UserInfo.setCellData(0, "sJobCode", wconv.trim(rOldJobCod)); // 본인)직업명 ds에 등록
    Ds_UserInfo.setCellData(0, "sNewJobCode", wconv.trim(Edt_sJobCode.getValue())); // 본인)직업코드 ds에 등록 		
    Ds_UserInfo.setCellData(0, "sJobNam", wconv.trim(Edt_sJobNam.getValue())); // 본인)직업코드 ds에 등록 		
    //  청약에 보내주기  
    Ds_UserInfo.setCellData(0, "sDriveClass", wconv.trim(rDrvCls)); // 본인)직업코드 ds에 등록 		
    Ds_UserInfo.setCellData(0, "sJobClass", wconv.trim(rJobCls)); // 본인)직업코드 ds에 등록 
    //  Edt_sJobCode(그룹 코드) 01 이면  팝업창 띄우기  
    if (wconv.substr(Edt_sJobCode.getValue(), 0, 2) == "01") {
        var jobargs = Edt_sJobCode.getValue() + "^" + Edt_sJobNam.getValue();
        promise = promise.then(function(rtnVal) {
            //직업 코드 
            return wprom.promise.popupOpen("PROJECT_PATH_TEMP/cut/fCut01015s.xml", "jobargs=" + jobargs /*wprom.popupOpen(가이드참조)*/ , 100, 100, true, 400, 200);
        });
        promise = promise.then(function(reVal) {
            var retArr = wconv.split(reVal, "^");
            if (wconv.trim(wconv.length(retArr[0])) > 2) {
                // 데이터 화면
                //Ed_sSchoolCode.setValue(		= trim(retArr[0]);
                //Ed_sSchoolName.setValue(		= trim(retArr[1]);
                //dataTab.tab1.Edt_2_Zip1.TesetValue(xt	= trim(retArr[2]);	
