                //dataTab.tab1.Edt_2_sAdrs1.Text	= trim(retArr[4])+" "+trim(retArr[5])+" "+trim(retArr[6]) ;	
                //dataTab.tab1.Edt_2_AdrsAdd.Text	= trim(retArr[7])+" "+trim(retArr[1]) ;	
                //Ds_AddrOffice.setCellData(0, "cType","insert");
                Ds_AddrOffice.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
                Ds_AddrOffice.setCellData(0, "sZip1", wconv.trim(retArr[2]));
                Ds_AddrOffice.setCellData(0, "sZip2", wconv.trim(retArr[3]));
                Ds_AddrOffice.setCellData(0, "sAdrs1", wconv.trim(retArr[4]));
                Ds_AddrOffice.setCellData(0, "sAdrs2", wconv.trim(retArr[5]));
                Ds_AddrOffice.setCellData(0, "sAdrs3", wconv.trim(retArr[6]));
                Ds_AddrOffice.setCellData(0, "sAdrsAdd", dataTab_tab1_Edt_2_AdrsAdd.getValue());

                //Ds_UserInfo.setCellData(0, "sJobGroup", trim(substr(rOldJobCod,0,2)));	// 본인)직업코드 ds에 등록 
                //Ds_UserInfo.setCellData(0, "sJobCode", trim(rOldJobCod));	// 본인)직업명 ds에 등록
                //Ds_UserInfo.setCellData(0, "sNewJobCode", trim(Edt_sJobCode.Text));	// 본인)직업코드 ds에 등록 		
                //Ds_UserInfo.setCellData(0, "sJobNam", trim(Edt_sJobNam.Text));		// 본인)직업코드 ds에 등록 		
                //Ds_UserInfo.setCellData(0, "sSchoolCode", trim(retArr[0]));
                //Ds_UserInfo.setCellData(0, "sSchoolName", trim(retArr[1]));
                //Ds_UserInfo.setCellData(0, "sMutualYn", trim(retArr[8]));		// 공제회원여부 
