/**
 * @function	telHistoryPopup
 * @desc		연락처 History 오픈
 * @param      
 * 				str 
 * 	str  변수값 정의
 * 	arg1  = custNo
 * 	arg2  = seqNo
 * 	arg3  = DataSet
 * 	arg4  = row
 * 	arg5  = column 집전화1
 * 	arg6  = column 집전화2
 * 	arg7  = column 집전화3
 * 	arg8  = column 직장전화1
 * 	arg9  = column 직장전화2
 * 	arg10 = column 직장전화3
 * 	arg11 = column 휴대전화1
 * 	arg12 = column 휴대전화2
 * 	arg13 = column 휴대전화3
 * 	arg14 = column 기타1
 * 	arg15 = column 기타2
 * 	arg16 = column 기타3
 * 	arg17 = column 기타구분
 * 	arg18 = column Fax1
 * 	arg19 = column Fax2
 * 	arg20 = column Fax3
 * 	arg21 = column Eamil1
 * 	arg22 = column Eamil2
 * 	arg23 = 포커스 이동할 컴포넌트
 **/
scwin.telHistoryPopup = function(str) {
    // 전화번호 히스토리 오픈
    var indata = {"parentInfo":str};
    var popInfo = {
       			title : "연락처 History 조회",
				width : 738,
				height : 330,
				indata : indata    
    };
    //open("com_cut::OCU10200400s_P03.xml", "parentInfo="+str, 100, 100, true, 300, 100);
    //open("cst_cst::A085_Pop.xml", "parentInfo=" + str, 100, 100, true, 300, 100);
    //wprom.windowOpen("/ui/cbso/CBSO026P.xml",popInfo)
    wprom.popupOpen("/ui/cbso/CBSO026P.xml",popInfo);
};

