// 행삭제 
scwin.btn_insrdDel_OnClick = function(obj) {
    var nRow = Ds_GENAC15.getFocusedRowIndex();
    if (nRow == -1) {
        wprom.alert("삭제할 피보험자 선택된 행이 없습니다");
        return;
    } else {
        util.deleteRow(Ds_GENAC15, nRow);
    }
};

