    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_me_sMainCustNo_OnKillFocus
// 내용        : 포커스 아웃시 주민번호 형식 체크
//---------------------------------------------------------------------------
scwin.lfn_me_sMainCustNo_OnKillFocus = function(e) {
//console.log("================="+e.id);
    if (wconv.length(me_sMainCustNo.getValue()) > 0) {
        if (!checkPersonID(me_sMainCustNo.getValue())) {
            wprom.alert("주민등록번호가 형식에 맞지않습니다.");
        }
    }
};

scwin.Btn_Ntl_OnClick = function(e) {
    var promise = Promise.resolve();
    var sParam = "";
    var sCdNameFlag;
    var sSerchKey;
    if (wconv.trim(wconv.length(Edt_sBossNationName.getValue())) > 1) {
        sCdNameFlag = "Name";
        sSerchKey = Edt_sBossNationName.getValue();
    } else {
        sCdNameFlag = "Cd";
        sSerchKey =  Edt_sBossNation.getValue();
    }
    promise = promise.then(function(rtnVal) {
    	var indata = {
    			"sCdNameFlag" :sCdNameFlag,
    			"sSerchKey" : sSerchKey
    			};
    	var popInof = {
				width : 412,
				height : 440,
				"indata" : indata,
				"title":"국적조회"    	
    	};
        return wprom.popupOpen("/ui/cbso/CBSO027P.xml", popInof);
    });
    promise = promise.then(function(rst) {
        if(rst != false){
	        var NtnlInfo = rst.split( "^" );
	        Edt_sBossNation.setValue(NtnlInfo[0]);
	        Edt_sBossNationName.setValue(NtnlInfo[1]);
	    }    
        return;
    });
    return promise;
};

//---------------------------------------------------------------------------
// function 명 : lfn_getCUSAA42Info()
// 내용        : 가입설계 동의여부 확인 
//---------------------------------------------------------------------------
scwin.lfn_getCUSAA42Info = function(insrdID) {
    //신용정보 동의 23조 동의여부 확인	 
    wconv.clear(DsWs_Search42);
    var RejectYN = "";
    // 가입설계 동의 여부 체크 
    /*
    SOBJ_Search.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.customer.task.CustTask", "getCUSAA42Info", 0, 0);
    gfn_SetParam(MethodObj, "sCustNo", insrdID);
    gfn_SetParam(MethodObj, "sProductType", "3");
    var result = gfn_CallService(SOBJ_Search, DOBJ_Search, MethodObj);
    */
	var MethodObj = new callService("com.inswave.customer.task.CustTask", "getHandlerCUSAA42Info");
	MethodObj.setId("getHandlerCUSAA42Info");
	MethodObj.setParam("sCustNo", insrdID);
	MethodObj.setParam("sProductType", "3");
	MethodObj.setParam("sHandlerID", sHandlerID); //추가된 param
	MethodObj.setUrl("/common/CBSO012PController.do");
	MethodObj.setXPath("//*/CUSAA42");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA42"), "DsWs_Search42");
	util.setCompBindRefresh("DsWs_Search42");
	util.setRowPosition(DsWs_Search42, 0);

    //trace(replace(gfn_XmlEncoder(DOBJ_Search.Value), "><", ">\n<"));
    if (DsWs_Search42.getCellData(0, "result") > 0) {
        if (DsWs_Search42.getCellData(0, "sAgmReq") == "Y") {
            RejectYN = "Y"; //유효일자가 지난경우 다시 받기. 해당 칼럼이 다 체크해줌
            //2016.09.05(N1609-00035)
            //if (!IsExistVar(parentWin.sPlanNo, true)) {
            if(!util.isNull(fv_args.sPlanNo)){
                if (wconv.left(fv_args.sPlanNo, 1) == "N") {
                    RejectYN = "";
                }
            }
        }
    } else {
        RejectYN = "Y"; //가입동의 거부
    }
    return RejectYN;
};

//---------------------------------------------------------------------------
// function 명 : lfn_getCUSAA0441Info()
// 내용        : 상품설계  동의여부 확인 
//---------------------------------------------------------------------------
scwin.lfn_getCUSAA0441Info = function(insrdID) {
    var RejectYN = "";
    // 가입설계 동의 여부 체크 
    wconv.clear(DsWs_Search41);
    wconv.clear(DsWs_Search04);
    /*
    SOBJ_Search.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.customer.task.CustTask", "getCUSAA0441Info", 0, 0);
    gfn_SetParam(MethodObj, "sCustNo", insrdID);
    var result = gfn_CallService(SOBJ_Search, DOBJ_Search, MethodObj);
    */
	var MethodObj = new callService("com.inswave.customer.task.CustTask", "getCUSAA0441Info");
	MethodObj.setId("getHandlerCUSAA0441Info");
	MethodObj.setParam("sCustNo", insrdID);
	MethodObj.setParam("sHandlerID", sHandlerID);  //추가된 param
	MethodObj.setUrl("/common/CBSO012PController.do");
	MethodObj.setXPath("//*/CUSAA41");
	MethodObj.addXPath("//*/CUSAA42");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA41"), "DsWs_Search41");
	util.setCompBindRefresh("DsWs_Search41");
	util.setRowPosition(DsWs_Search41, 0);
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA42"), "DsWs_Search04");
	util.setCompBindRefresh("DsWs_Search04");
	util.setRowPosition(DsWs_Search04, 0);

    //trace(replace(gfn_XmlEncoder(DOBJ_Search.Value), "><", ">\n<"));
    //trace(DsWs_Search04.SaveXML());
    //trace(DsWs_Search41.SaveXML());
    //trace(DsWs_Search04.SaveXML());
    RejectYN = "Y"; //가입동의 거부(Y인 경우 동의 필요)
    if (DsWs_Search04.getRowCount() > 0 && DsWs_Search41.getRowCount() > 0) {
        if (DsWs_Search41.getCellData(0, "sAgmReq") == "N" && DsWs_Search04.getCellData(0, "sAgmReq") == "N") {
            RejectYN = "N";
        }
    } else {
        RejectYN = "Y"; //가입동의 거부(Y인 경우 동의 필요)
    }


    // 상품동의 여부 체크 (전화번호 및 메일 변경시)
    if (sGoodAgreeFlag == "Y" && wconv.length(sGoodsInTroYN) > 0) {
        sGoodAgreeFlag = "N";
    }
    return RejectYN; 
};

//신용정보 
//---------------------------------------------------------------------------
// function 명 : btn_Confidence_OnClick()
// 내용        : 가입설계 동의 팝업
//---------------------------------------------------------------------------
scwin.btn_Confidence_OnClick = function(obj) {
    var promise = Promise.resolve();
    //1248100998
    if (wconv.length(me_sMainCustNo.getValue()) < 13) {
        wprom.alert("대표자 주민번호를 입력하세요");
        return;
    }
    var resultArr = "";
    var agree23 = "";
    var agree24 = "";
    var sProductType = "3";
    var argss = me_sMainCustNo.getValue() + "^" + Edt_sBossName.getValue() + "^" + sProductType;
    promise = promise.then(function(rtnVal) {
        //var rtn = Dialog("com_cut::OCU10200400s_P05.xml","argss="+argss,100, 100, true, 400, 200);
        //var rtn = Dialog("ext_com::A095_Pop.xml","argss="+argss,100, 100, true, 400, 200);	
        var indata = {"argss" : argss};
        var popInfo = {
       			title : "가입설계를 위한 개인(신용)정보처리 동의",
				width : 654,
				height : 385,
        		"indata":indata
        		};
        return wprom.popupOpen("/ui/cbso/CBSO044P.xml", popInfo);
    });
    promise = promise.then(function(rtn) {
        //가입설계 동의 여부 재조회
        if (scwin.lfn_getCUSAA42Info(me_sMainCustNo.getValue()) == "Y") {
            //btn_InsDesign.Color = "user40";
            //btn_InsDesign.Font = "굴림,9,Bold";
        } else {
            //btn_InsDesign.Color = "user41";
            //btn_InsDesign.Font = "굴림,9";
        }
        if (wconv.length(rtn) > 1) {
            resultArr = rtn.split( "^");
            agree23 = wconv.trim(resultArr[0]);
            agree24 = wconv.trim(resultArr[1]);
        }


        // true 이면  신용정보 조회 한 결과 
        //if(rtn ="true")
        if (agree23 == "true" && agree24 == "true") {
            Ds_CUSBA01.setCellData(0, "sCustNo", custNo);
            Ds_CUSBA01.setCellData(0, "sExpired", "");
        }
    });
    return promise;
};

//---------------------------------------------------------------------------
// function 명 : btn_GoodsInTro_OnClick()
// 내용        : 상품소개 동의 팝업
//---------------------------------------------------------------------------
scwin.btn_GoodsInTro_OnClick = function(obj) {
    var promise = Promise.resolve();
    var resultArr = "";
    if (wconv.length(me_sMainCustNo.getValue()) < 13) {
        wprom.alert("대표자 주민번호를 입력하세요");
        return;
    }

    var argss = me_sMainCustNo.getValue() + "^" + Edt_sBossName.getValue();
    promise = promise.then(function(rtnVal) {
        //var rtn = Dialog("com_cut::OCU10200400s_P05.xml","argss="+argss,100, 100, true, 400, 200);
        //var rtn = Dialog("ext_com::A095_Pop.xml","argss="+argss,100, 100, true, 400, 200);	
        var indata = {"argss" : argss};
        var popInfo = {
        		"indata":indata
        		};
        return wprom.popupOpen("/ui/common/CBSO045P.xml",popInfo);
    });
    promise = promise.then(function(rtn) {
        //trace("rtn::::::::::::::"+rtn);
        sGoodsInTroYN = rtn;
        // 상품소개 동의 여부 재체크 
        if (scwin.lfn_getCUSAA0441Info(me_sMainCustNo.getValue()) == "Y") {
            //btn_GoodsIntro.Color = "user40";
            //btn_GoodsIntro.Font = "굴림,9,Bold";
        } else if (sGoodAgreeFlag == "N") {
            //btn_GoodsIntro.Color = "user41";
            //btn_GoodsIntro.Font = "굴림,9";
        }
    });
    return promise;
};
