scwin.getAuthList = function() {
    //개별권한
    /*origin
    var MethodObj = gfn_CreateMethodObj(SOBJ_Admin, "com.inswave.claim.task.ClaimAdminTask", "getAllAuth", "", "");
    gfn_SetParam(MethodObj, "sAuthType", "A");
    gfn_CallService(SOBJ_Admin, DOBJ_AllAuth, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.claim.task.ClaimAdminTask", "getAllAuth");
	MethodObj.setId("getAllAuth");
	MethodObj.setParam("sAuthType", "A");
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAA13");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	DsWs_UpperOrg.setJSON(_tranSync.getDsJson("ADMAA13")); // dataset 데이터 	
	/*
    //그룹권한
    var MethodObj = gfn_CreateMethodObj(SOBJ_Admin, "com.inswave.admin.task.AuthTask", "selectAuthGroups", "", "");
    gfn_CallService(SOBJ_Admin, DOBJ_AllAuthGroups, MethodObj);
    */
	var MethodObj1 = new callService("com.inswave.admin.task.AuthTask", "selectAuthGroups");
	MethodObj1.setId("selectAuthGroups");
	MethodObj1.setUrl("/common/CBSO014MController.do");
	MethodObj1.setXPath("//*/vector/data/ADMAA09");
	MethodObj1.addXPath("//WARNING");
	MethodObj1.sync();
	MethodObj1.send();
	DsWs_getTeam.setJSON(_tranSync.getDsJson("ADMAA09")); // dataset 데이터 	

    var nRow = util.appendRow(DsWs_getTeam);
    DsWs_getTeam.setCellData(nRow, "nAuthGroupID", "0");
    DsWs_getTeam.setCellData(nRow, "sAuthGroupName", "개별"); 
    //trace(DsWs_UpperOrg.SaveXML());
    //trace(DsWs_getTeam.SaveXML());
};

scwin.getDutyPosition = function() {
    /**
     * 파트,업무 공통코드
     *  - gfn_getCommonCODAA01(erviceObject 객체, DataObject 객체, Dataset 아이디, 공통코드 main아이디, 컴포넌트 아이디, --선택-- 표시여부(Y,N));
     *  - 해당 데이터셋의 'Editable' 속성은 false로 설정해야 한다.
     *  - innerDataset이나 comboDataset의 code값은 'sMainCode', value값은 'sShortName'으로 세팅해야 한다.
     **/
    var dataSetStr = "Ds_GetAllAuth Ds_AuthDiv"; //해당 데이터셋의 'Editable' 속성은 false로 설정해야 한다.
    var codTypeStr = "86 87";
    var componentIds = "tab0_tab1_Cmb_AllAuth tab0_tab1_Cmb_sAuthType2";
    var selectAdds = "N N";
    gfn_getCommonCODAA02(dataSetStr, codTypeStr, componentIds, selectAdds);

    //trace("DOBJ_Common AA ===>> " + gfn_XmlEncoder(DOBJ_GetAllAuth.Value)	);
    //trace("DOBJ_Common AA ===>> " + Ds_GetAllAuth.SaveXML()	);
    //필요시에 해당 데이터셋 필터링.
    //Ds_Position.Filter("sClassCode=='00'"); 
    //필터링 후 '--선택--' 등도 필터가 되기 때문에 필요시 삽입.
    //Ds_Position.InsertRow(0);
    //Ds_Position.SetColumn(0, "sMainCode", "");
    //Ds_Position.SetColumn(0, "sShortName", "--선택--");
    //trace("before ===>> " + Ds_AuthDiv.SaveXML() );
    var insRow = util.insertRow(Ds_AuthDiv, 0);
    Ds_AuthDiv.setCellData(0, "sMainCode", "");
    Ds_AuthDiv.setCellData(0, "sShortName", "전체");
    //trace("after ===>> " + Ds_AuthDiv.SaveXML()	);
    var insRow1 = util.insertRow(Ds_GetAllAuth, 0);
    Ds_GetAllAuth.setCellData(0, "sMainCode", "");
    Ds_GetAllAuth.setCellData(0, "sShortName", "전체");
    Tab0_tab1_Cmb_AllAuth.setSelectedIndex(0);
    Tab0_tab1_Cmb_sAuthType2.setSelectedIndex(0);
};

//**************************************************************************************************
// TAB OnChanged
//**************************************************************************************************
scwin.Tab0_OnChanged =  function (tabID, idx, userTabID){
    if (idx == 0 || idx == 2) {
        gfn_btnEnable( false, false, false, false, false, false, true, true);
    } else if (idx == 1) {
        gfn_btnEnable( false, false, false, false, false, false, false, true);
    }
    return;
	//아래부분 사용안됨.     
    if (idx == 0 || idx == 0) {
        sType = "list";
        gfn_btnEnable(true, false, false, false, false, false, false, true, true);
    } else if (idx == 1) {
        if (sType == "list")
            Tab0.setSelectedIndex(0);
        else
            gfn_btnEnable(true, false, false, false, false, false, false, false, true);
    }
    return;
    if (sType == "group")
        Tab0.setSelectedIndex(0);
    else
        Tab0.setSelectedIndex(1)	;
    return;
    if (idx == 0) {
        //----- 버튼 처리(조회, 입력, 수정, 삭제, 결제, 취소, 인쇄, 엑셀, 닫기)
        gfn_btnEnable(false, false, false, false, false, false, false, false, true);
    } else if (idx == 1) {
        Ds_AuthSave.removeAll();
        var insRow = util.insertRow(Ds_AuthSave, 0);
        Tab0_tab2_Cmb_sAuthType.setValue( "");

        //----- 버튼 처리(조회, 입력, 수정, 삭제, 결제, 취소, 인쇄, 엑셀, 닫기)
        if (gfn_checkAuthByID(util.getGdsValue("gUserID"), "60000"))
            gfn_btnEnable(false, true, false, false, false, false, false, false, true);
        else
            gfn_btnEnable(false, false, false, false, false, false, false, false, true);
    } else if (idx == 2) {
        if (Ds_GetAuthInfo.getRowCount() == 0) {
            Tab0.setSelectedIndex(nOldIndex);
            return;
        }


        //----- 버튼 처리(조회, 입력, 수정, 삭제, 결제, 취소, 인쇄, 엑셀, 닫기)
        if (gfn_checkAuthByID(util.getGdsValue("gUserID"), "60000"))
            gfn_btnEnable(false, false, true, false, false, false, false, false, true);
        else
            gfn_btnEnable(false, false, false, false, false, false, false, false, true);
    } else if (idx == 3) {
        if (nOldIndex == 2) {
            Tab0_tab4_Cmb_AllAuth.setValue( Ds_GetAuthInfo.getCellData(0, "nAuthID"));
        }
        Ds_AuthSave.removeAll();
        var insRow = util.insertRow(Ds_AuthSave, 0);
        //Ds_User2.Filter("FTX1=='A'");
        //Ds_User3.Filter("FTX1=='A'");
        //----- 버튼 처리(조회, 입력, 수정, 삭제, 결제, 취소, 인쇄, 엑셀, 닫기)
        if (gfn_checkAuthByID(util.getGdsValue("gUserID"), "60000"))
            gfn_btnEnable(true, false, false, false, false, false, false, false, true);
        else {
            gfn_btnEnable(false, false, false, false, false, false, false, false, true);
            Tab0_tab4_btn_add.setDisabled(!false);
            Tab0_tab4_btn_del.setDisabled(!false);
        }
    }
};

