scwin.GetAllAuth = function() {
    wconv.clear(DsWs_AuthList);
	var MethodObj = new callService("com.inswave.claim.task.ClaimAdminTask", "getAllAuth");
	MethodObj.setId("getAgentList");
	MethodObj.setParam("sAuthType", "A");
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAA13");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_AuthList.setJSON(_tranSync.getDsJson("ADMAA13")); // dataset 데이터     
};

