scwin.Tab0_tab1_Grid0_OnCellClick = function(row,col) {
    var promise = Promise.resolve();
    if (col == 0 || col == 1) {
    } else if (col == 2) {
        // 사원코드
        scwin.view_userdetail(DsWs_UserAuth.getCellData(row, "sUserID"), DsWs_UserAuth.getCellData(row, "sUserName"));
    } else if (nCell == 11) {
        promise = promise.then(function(rtnVal) {
            // 권한코드
            return scwin.popup_userMenuByAuth(DsWs_UserAuth.getCellData(nRow, "nAuthID")
                                                               , DsWs_UserAuth.getCellData(nRow, "sAuthName")
                                                               , DsWs_UserAuth.getCellData(nRow, "sDepartCode")
                                                               , DsWs_UserAuth.getCellData(nRow, "sDepartName")
                                                               , DsWs_UserAuth.getCellData(nRow, "sDescription")
                                                               , DsWs_UserAuth.getCellData(nRow, "sApplySystem"));
        });
    }
    return promise;
};

scwin.GetDetail = function(nRow) {
    Ds_GetAuthInfo.removeAll();
    util.appendRow(Ds_GetAuthInfo);
    Ds_GetAuthInfo.setCellData(0, "nAuthID", Ds_GetAllAuth.getCellData(nRow, "nAuthID"));
    Ds_GetAuthInfo.setCellData(0, "sAuthName", Ds_GetAllAuth.getCellData(nRow, "sAuthName"));
    Ds_GetAuthInfo.setCellData(0, "sAuthType", Ds_GetAllAuth.getCellData(nRow, "sAuthType"));
    Ds_GetAuthInfo.setCellData(0, "sDescription", Ds_GetAllAuth.getCellData(nRow, "sDescription"));
    Ds_GetAuthInfo.setCellData(0, "sDepartCode", Ds_GetAllAuth.getCellData(nRow, "sDepartCode"));
    Ds_GetAuthInfo.setCellData(0, "sDepartName", Ds_GetAllAuth.getCellData(nRow, "sDepartName"));
    //trace(Ds_GetAuthInfo.SaveXML());
    Tab0.setSelectedIndex(2);
};

scwin.Tab0_tab3_Btn_MoveTab3_OnClick = function(obj) {
    Tab0.setSelectedIndex(3);
};

