scwin.Tab0_tab4_cmb_AuthType2_OnChanged = function(info){ //obj, strCode, strText, nOldIndex, nNewIndex) {
    if (info.newValue == "0000000") {
        Ds_GetAllAuth.removeColumnFilterAll();
    } else {
        wconv.filter(Ds_GetAllAuth, "sAuthType=='" + info.newValue + "'",true); //	trace(Ds_GetAllAuth.SaveXML());	
    }
    Tab0_tab4_Cmb_AllAuth.Index = 0;
};

/* creat 10.11.12 박대성 
 */
scwin.Tab0_tab4_cmb_User3_OnChanged = function(info){//obj, info.newValue, strText, nOldIndex, nNewIndex) {
    if (info.newValue == "0000000") {
        scwin.Init_DataSet("Tab0.tab4.Cmb_User4", "Ds_User4");
    } else {
        scwin.getOrgList("Tab0.tab4.Cmb_User4", "Ds_User4", "sPart", "sGroup", info.newValue);
    }
};

/* update 10.11.12 박대성 
 * 기존의 DataSet에 강제 Coding 되어 있는 Data를 Filtering 하는게 아닌 QUERY DATA 조회를 통회 Display
 */
scwin.Tab0_tab4_cmb_User2_OnChanged = function(info){
    /*var fiterVal = Ds_User1.GetColumn(Ds_User1.FindRow("CODE", info.newValue), "FTX1");	
    Ds_User2.Filter("FTX1=='A'||FTX1=='"+fiterVal+"'");
    Tab0.tab4.Cmb_User2.Index = 0;
    Ds_User3.Filter("FTX1=='A'");
    Tab0.tab4.Cmb_User3.Index = 0;*/
    if (info.newValue == "0000000") {
        scwin.Init_DataSet("Tab0.tab4.Cmb_User3", "Ds_User3");
        scwin.Init_DataSet("Tab0.tab4.Cmb_User4", "Ds_User4");
    } else {
        scwin.getOrgList("Tab0.tab4.Cmb_User3", "Ds_User3", "sGroup", "sTeam", info.newValue);
    }
};

/* update 10.11.12 박대성 
 * 기존의 DataSet에 강제 Coding 되어 있는 Data를 Filtering 하는게 아닌 QUERY DATA 조회를 통회 Display
 */
scwin.Tab0_tab4_cmb_User1_OnChanged = function(info){//obj, info.newValue, strText, nOldIndex, nNewIndex) {
    /*var fiterVal = Ds_User2.GetColumn(Ds_User2.FindRow("CODE", info.newValue), "FTX2");
    Ds_User3.Filter("FTX1=='A'||FTX1=='"+fiterVal+"'");
    Tab0.tab4.Cmb_User3.Index = 0;*/
    if (info.newValue == "0000000") {
		scwin.Init_DataSet("Tab0.tab4.Cmb_User2", "Ds_User2");
        scwin.Init_DataSet("Tab0.tab4.Cmb_User3", "Ds_User3");
        scwin.Init_DataSet("Tab0.tab4.Cmb_User4", "Ds_User4");
    } else {
        scwin.getOrgList("Tab0.tab4.Cmb_User2", "Ds_User2", "sTeam", "sCenter", info.newValue);
    }
};

/* Creat 10.11.12 박대성
 * 해당 값에 따른 팀,그룹,파트 DATA 조회
 * obj : 객체명
 * sDataSet : 조회된 DATA 담을 DataSet
 * sDepartCode : 추출한 DATA 범위
 * sLevel : 상위 그룹
 * sUpperCode : 검색값
 */
scwin.getOrgList = function(obj, sDataSet, sDepartCode, sLevel, sUpperCode) {
    object(sDataSet).Clear();
    wconv.clear(DsWs_SearchCode);
    SOBJ_SearchCode.WSDL = gds.gWsdlClaim;
    var MethodObj = gfn_CreateMethodObj(SOBJ_SearchCode, "com.inswave.admin.task.AdminTask", "getOrgListbyUpDpt", "", "");
    gfn_SetParam(MethodObj, "sDepartCode", sDepartCode);
    gfn_SetParam(MethodObj, "sLevel", sLevel);
    gfn_SetParam(MethodObj, "sUpperCode", sUpperCode);
    gfn_CallService(SOBJ_SearchCode, DOBJ_SearchCode, MethodObj);

    //trace(replace(gfn_XmlEncoder((DOBJ_SearchCode.Value)), "><", ">\n<"));
    //trace(DsWs_SearchCode.SaveXML());
    object(sDataSet).Copy("DsWs_SearchCode");
    //trace(object(sDataSet).SaveXML());		
    object(sDataSet).InsertRow(0);
    object(sDataSet).setCellData(0, "sDepartCode", "0000000");
    object(sDataSet).setCellData(0, "sDepartName", "전체");
    object(obj).setSelectedIndex(0);
};

/* Creat 10.11.12 박대성
 * 사원구분 Combo 창 Initial
 * obj : 객체명
 * sDataSet : 조회된 DATA 담을 DataSet
 */
scwin.Init_DataSet = function(obj, sDataSet) {
    object(sDataSet).Clear();
    object(sDataSet).AddColumn("sDepartCode");
    object(sDataSet).AddColumn("sDepartName");
    object(sDataSet).InsertRow(0);
    object(sDataSet).setCellData(0, "sDepartCode", "0000000");
    object(sDataSet).setCellData(0, "sDepartName", "전체");
    object(obj).setSelectedIndex(0);
};

scwin.Tab0_tab4_common_OnHeadClick = function(obj, nCell, nX, nY, nPivotIndex) {
    if (nCell != 0) {
        return;
    }
    var txt = obj.GetCellText("head", 0, nCell);
    if (txt == "0") {
        for (var i = 0; i < object(obj_BindDataset).getRowCount(); i++) {
            object(obj_BindDataset).setCellData(i, "CHK", "1");
        }
        obj.SetCellProp("head", nCell, "text", "1");
    } else {
        for (var i = 0; i < object(obj_BindDataset).getRowCount(); i++) {
            object(obj_BindDataset).setCellData(i, "CHK", "0");
        }
        obj.SetCellProp("head", nCell, "text", "0");
    }
};
//비동기 방식 호출 
scwin.lfn_Search_async = function(e) { //완료
	util.eventAsyncCall(this, scwin.lfn_Search, arguments);	
}
// 조회, 저장, 수정 버튼
scwin.lfn_Search = function() 
{
    if (Tab0.getSelectedTabIndex() == 0) {
        scwin.get_list();
        gfn_btnEnable(false, false, false, false, false, false, true, true);
        btn_search.setDisabled(!true);
    } else if (Tab0.getSelectedTabIndex() == 1) {
        scwin.get_detail();
        gfn_btnEnable( false, false, false, false, false, false, false, true);
    } else if (Tab0.getSelectedTabIndex() == 2) {
        scwin.get_menuList();
        gfn_btnEnable(false, false, false, false, false, false, true, true);
    }
    return;
};

scwin.Tab0_tab4_Btn_Add_OnClick = function(obj) {
    wconv.copy(Ds_UserSave, "Ds_SourceList");
    wconv.filter(Ds_UserSave, "CHK=='1'",true);
    if (Ds_UserSave.getRowCount() == 0) {
        wprom.alert("입력 대상을 선택하세요.");
        return;
    }


    //-----서버 시간 받기
    transaction("AddUser", "theK::getServerTime.jsp", "", "gDs_ServerTime=output", "", "tr_callback");
};

scwin.Tab0_tab4_Btn_Del_OnClick = function(obj) {
    wconv.copy(Ds_UserSave, "Ds_TargetList");
    wconv.filter(Ds_UserSave, "CHK=='1'",true);
    if (Ds_UserSave.getRowCount() == 0) {
        wprom.alert("삭제 대상을 선택하세요.");
        return;
    }
    var nAuthID = Tab0_tab4_Cmb_AllAuth.getValue();

    //var inputStr = "<vector task='AuthTask' action='deleteAuthInfo' debug='false'>";
    var inputStr = "<vector task=\"ClaimAdminTask\" action=\"deleteAuthInfo\" debug=\"false\">";
    for (var i = 0; i < Ds_UserSave.getRowCount(); i++) {
        inputStr += "<data vectorkey=\"" + i + "\">";
        inputStr += "<ADMAA04>";
        inputStr += "<sUserID value=\"" + Ds_UserSave.getCellData(i, "sUserID") + "\"/>";
        inputStr += "<nAuthID value=\"" + nAuthID + "\"/>";
        inputStr += "</ADMAA04>";
        inputStr += "</data>";
    }
    inputStr += "</vector>";

    //trace(replace(inputStr, "><", ">\n<"));
    wconv.clear(DsWs_DelUserWarning);
    //SOBJ_DelUser.WSDL = gWsdlAdmin;
    SOBJ_DelUser.WSDL = gds.gWsdlClaim;
    var MethodObj = SOBJ_DelUser.CreateMethodObj("perform");
    SOBJ_DelUser.CreateObj("in0");
    MethodObj.in0 = inputStr;
    gfn_CallService(SOBJ_DelUser, DOBJ_DelUser, MethodObj);
    if (DsWs_DelUserWarning.getRowCount() != 0) {
        wprom.alert(DsWs_DelUserWarning.getCellData(0, "msg"));
        return;
    }


    //trace(replace(gfn_XmlEncoder((DOBJ_DelUser.Value)), "><", ">\n<"));
    scwin.lfn_Search();
};

//**************************************************************************************************
// CallBack
//**************************************************************************************************
scwin.tr_callback = function(ServiceId, errorCd, errorMsg) {
    if (ServiceId == "AddUser") {
        if (errorCd != 0)
            wprom.alert("errorCd:::[" + errorCd + "] errorMsg:::[" + errorMsg + "]");
        else {
            //trace(gDs_ServerTime.savexml());
            var nAuthID = Tab0_tab4_Cmb_AllAuth.getValue();
            var nAuthType = Ds_GetAllAuth.getCellData(wconv.findRow(Ds_GetAllAuth, "nAuthID", nAuthID), "sAuthType");

            //var inputStr = '<vector task="AuthTask" action="insertAuthInfo" debug="false">';
            var inputStr = "<vector task=\"ClaimAdminTask\" action=\"insertAuthInfo\" debug=\"false\">";
            for (var i = 0; i < Ds_UserSave.getRowCount(); i++) {
                inputStr += "<data vectorkey=\"" + i + "\">";
                inputStr += "<ADMAA04>";
                inputStr += "<nAuthType value=\"2\"/>";
                inputStr += "<sUserID value=\"" + Ds_UserSave.getCellData(i, "sUserID") + "\"/>";
                inputStr += "<nAuthID value=\"" + nAuthID + "\"/>";
                inputStr += "<sDuty value=\"" + Ds_UserSave.getCellData(i, "sDuty") + "\"/>";
                inputStr += "<sDepartCode value=\"" + Ds_UserSave.getCellData(i, "sDepartCode") + "\"/>";
                inputStr += "<sInputDate value=\"" + wconv.subStr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8) + "\"/>";
                inputStr += "</ADMAA04>";
                inputStr += "</data>";
            }
            inputStr += "</vector>";

            //trace(replace(inputStr, "><", ">\n<"));
            wconv.clear(DsWs_AddUserWarning);
            //SOBJ_AddUser.WSDL = gWsdlAdmin;
            SOBJ_AddUser.WSDL = gds.gWsdlClaim;
            var MethodObj = SOBJ_AddUser.CreateMethodObj("perform");
            SOBJ_AddUser.CreateObj("in0");
            MethodObj.in0 = inputStr;
            gfn_CallService(SOBJ_AddUser, DOBJ_AddUser, MethodObj);
            if (DsWs_AddUserWarning.getRowCount() != 0) {
                wprom.alert(DsWs_AddUserWarning.getCellData(0, "msg"));
                return;
            }


            //trace(replace(gfn_XmlEncoder((DOBJ_AddUser.Value)), "><", ">\n<"));
            scwin.lfn_Search();
        }
    }
};

//**************************************************************************************************
// TAB2 등록
//**************************************************************************************************
scwin.lfn_Input = function() {
    //alert("Ds_AuthSave");
    if (wconv.length(Ds_AuthSave.getCellData(0, "nAuthID")) == 0) {
        wprom.alert("권한 ID를 입력하세요.");
        Tab0_tab2_Edit1.focus();
        return;
    }
    if (wconv.length(Ds_AuthSave.getCellData(0, "sAuthName")) == 0) {
        wprom.alert("권한 명을 입력하세요.");
        Tab0_tab2_Edit0.focus();
        return;
    }
    var inputStr = "";
    //inputStr += '<ADMAA03 task="AuthTask" action="insertAuth" debug="true">';
    inputStr += "<ADMAA13 task=\"ClaimAdminTask\" action=\"insertAuth\" debug=\"false\">";
    inputStr += "<nAuthID value=\"" + Ds_AuthSave.getCellData(0, "nAuthID") + "\"/>";
    inputStr += "<sAuthName value=\"" + gfn_XMLDecoder(Ds_AuthSave.getCellData(0, "sAuthName")) + "\"/>";
    //inputStr += '<sAuthType value="0"/>';
    inputStr += "<sAuthType value=\"" + Ds_AuthSave.getCellData(0, "sAuthType") + "\"/>"; //2011.08.30 김상열
    inputStr += "<sDepartCode value=\"" + Ds_AuthSave.getCellData(0, "sDepartCode") + "\"/>";
    inputStr += "<sDescription value=\"" + gfn_XMLDecoder(Ds_AuthSave.getCellData(0, "sDescription")) + "\"/>";
    //inputStr += '</ADMAA03>';
    inputStr += "</ADMAA13>";
    wconv.clear(DsWs_RegWarning);
    //SOBJ_Reg.WSDL = gWsdlAdmin;
    SOBJ_Reg.WSDL = gds.gWsdlClaim;
    var MethodObj = SOBJ_Reg.CreateMethodObj("perform");
    SOBJ_Reg.CreateObj("in0");
    MethodObj.in0 = inputStr;
    gfn_CallService(SOBJ_Reg, DOBJ_Reg, MethodObj);
    if (DsWs_RegWarning.getRowCount() != 0) {
        wprom.alert(DsWs_RegWarning.getCellData(0, "msg"));
        return;
    }


    //trace(replace(gfn_XmlEncoder((DOBJ_Reg.Value)), "><", ">\n<"));
    wprom.alert("입력 되었습니다.");
    Tab0.setSelectedIndex(0);
    scwin.GetAllAuth();
};

