scwin.lfn_Modify = function() {
    //alert("Ds_GetAuthInfo");
    if (wconv.length(Ds_GetAuthInfo.getCellData(0, "sAuthName")) == 0) {
        wprom.alert("권한 명을 입력하세요.");
        Tab0_tab3_Edit0.focus();
        return;
    }
    var inputStr = "";
    //inputStr += '<ADMAA03 task="AuthTask" action="updateAuth" debug="true">';
    inputStr += "<ADMAA13 task=\"ClaimAdminTask\" action=\"updateAuth\" debug=\"false\">";
    inputStr += "<nAuthID value=\"" + Ds_GetAuthInfo.getCellData(0, "nAuthID") + "\"/>";
    inputStr += "<sAuthName value=\"" + gfn_XMLDecoder(Ds_GetAuthInfo.getCellData(0, "sAuthName")) + "\"/>";
    inputStr += "<sAuthType value=\"" + Ds_GetAuthInfo.getCellData(0, "sAuthType") + "\"/>";
    inputStr += "<sDepartCode value=\"" + Ds_GetAuthInfo.getCellData(0, "sDepartCode") + "\"/>";
    inputStr += "<sDescription value=\"" + gfn_XMLDecoder(Ds_GetAuthInfo.getCellData(0, "sDescription")) + "\"/>";
    //inputStr += '</ADMAA03>';
    inputStr += "</ADMAA13>";
    wconv.clear(DsWs_RegWarning);
    //SOBJ_Reg.WSDL = gWsdlAdmin;
    SOBJ_Reg.WSDL = gds.gWsdlClaim;
    var MethodObj = SOBJ_Reg.CreateMethodObj("perform");
    SOBJ_Reg.CreateObj("in0");
    MethodObj.in0 = inputStr;
    gfn_CallService(SOBJ_Reg, DOBJ_Reg, MethodObj);
    if (DsWs_RegWarning.getRowCount() != 0) {
        wprom.alert(DsWs_RegWarning.getCellData(0, "msg"));
        return;
    }


    //trace(replace(gfn_XmlEncoder((DOBJ_Reg.Value)), "><", ">\n<"));
    wprom.alert("수정 되었습니다.");
    Tab0.setSelectedIndex(0);
    scwin.GetAllAuth();
};

