scwin.Tab0_tab2_btn_Pop_Search_OnClick = function(obj) {
    var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
        return wprom.popupOpen("PROJECT_PATH_TEMP/com_pop/0120.xml", "", 707, 321);
    });
    promise = promise.then(function(rst) {
        if (wconv.length(rst) != 0) {
            var arrRst =rst.split( "^");
            Tab0_tab2_edt_sDepartCode.setValue(  arrRst[0] );
            Tab0_tab2_edt_sDepartName.setValue( arrRst[1] );
        }
    });
    return promise;
};

