scwin.Tab0_tab1_Btn_MoveTab3_OnClick = function(obj) {
    if (Tab0_tab1_edit1.getValue() == "") {
        Ds_GetAllAuth.removeColumnFilterAll();
    } else {
        wconv.filter(Ds_GetAllAuth, "pos(sAuthName,'" + wconv.trim(Tab0_tab1_edit1.getValue()) + "') >== 0",true);
    }
};

scwin.Tab0_tab1_Edit1_OnKeyDown = function(obj, nChar, bShift, bCtrl, bAlt, LLParam, HLParam) {
    if (nChar == 13) {
        scwin.Tab0_tab1_Btn_MoveTab3_OnClick(obj);
    }
};
