scwin.uf_sEmpNoPop = function(_btn) {
    //var sEmpNo = tab0.tab1_Edit2;
    //var sEmpName = tab0.tab1_Edit5;
    /*
    if(!iPopUpOpenFlag) {
    	return;
    }

    iPopUpOpenFlag = false;
    */
    var sPopParm = " psButton=" + wconv.quote(_btn) + " sRowNum=" + "" + " sEmpNo=" + wconv.quote(Tab0_tab1_Edit2.getValue()) + " sEmpName=" + wconv.quote(Tab0_tab1_Edit5.getValue());
    var argArry = agf_Pop("사원목록", Tab0_tab1_Edit2, Tab0_tab1_Edit5, "", sPopParm);
    if (wconv.length(argArry) > 0) {
        if (argArry[0] == "noneClose") {
            Tab0_tab1_Edit2.setValue( "");
            Tab0_tab1_Edit5.setValue("");
            Tab0_tab1_Edit2.focus();
        }
    } else {
        Tab0_tab1_Edit2.focus();
    }
};

