        if (argArry[0] == "noneClose") {
            Tab0_tab1_edit1.setValue("");
            Tab0_tab1_edit1.focus();
        }
    } else {
        Tab0_tab1_edit1.focus();
    }
};
//사원목록
scwin.Tab0_tab1_Button1_OnClick = function(obj, args) {
    var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
        var sArgs = {};
		var popInfo = {
				"width"     : 523,
				"height"    : 515,		
				"indata"    : sArgs,
				"callback"  : "",
				"title"        : "사원목록"
			};           		
        return wprom.popupOpen("/ui/cbso/CBSO033P.xml", popInfo);
    });    
    promise = promise.then(function(result) {
        var rtnArry = result.split("^");
        if (result != 0) {
            Tab0_tab1_Edit2.setValue( rtnArry[0] );
            Tab0_tab1_Edit5.setValue( rtnArry[1] );
        } else {
            Tab0_tab1_Edit2.setValue( "" );
            Tab0_tab1_Edit5.setValue( "" );
        }
    });
    return promise;
};
// tab1.부서목록
scwin.Tab0_tab1_btn_Pop_Search_OnClick = function(e,args){//obj, args)
	var sThis = this; //버튼 object를 받기위해서 
    var promise = Promise.resolve();
    promise = promise.then(function(result) {
    	var sArgs = {};
		var popInfo = {
				"width"   : "583",
				"height"   : "535",
				"indata"    : sArgs,
				"callback"  : "",
				"title"        : "부서목록"
			};        
			return wprom.popupOpen("/ui/cbso/CBSO032P.xml", popInfo);
    });

    promise = promise.then(function(result) {
        var rtnArry = result.split( "^");
        if (sThis == Tab0_tab1_btn_Pop_Search) {
            if (rtnArry[0] > 0) {
                Tab0_tab1_Edit1.setValue(rtnArry[0]);
                Tab0_tab1_Edit4.setValue(rtnArry[1]);
            } else {
                Tab0_tab1_Edit1.setValue("");
                Tab0_tab1_Edit4.setValue("");
            }
        } else if (sThis == Tab0_tab3_btn_Pop_Search) {
            if (rtnArry[0] > 0) {
                Tab0_tab3_Edit1.setValue(rtnArry[0]);
                Tab0_tab3_Edit4.setValue(rtnArry[1]);
            } else {
                Tab0_tab3_Edit1.setValue("");
                Tab0_tab3_Edit4.setValue("");
            }
        }
    });

    return promise;
};
