scwin.uf_sEmpNoPop2 = function(_btn) {
    //alert("1");
    var sPopParm = " psButton=" + wconv.quote(_btn) + " sRowNum=" + "" + " sEmpNo=" + wconv.quote(Tab0_tab4_Edit10.getValue()) + " sEmpName=" + wconv.quote(Tab0_tab4_Edit12.getValue());

