        //alert("6");
        Tab0_tab4_Edit10.focus();
    }
};

scwin.view_userdata = function() {

	if(util.isNull(Tab0_tab4_Edit10.getValue())) return;

    var sEmpNo = Tab0_tab4_Edit10.getValue();
    wconv.clear(DsWs_Detail);
    wconv.clear(DsWs_DetailAuth);
    wconv.clear(Ds_Detail);
    /*orign
	//var MethodObj = gfn_CreateMethodObj(SOBJ_Claim, "com.inswave.admin.task.AuthTask", "getUserInfo", "", "");
    //gfn_SetParam(MethodObj, "sUserID", sEmpNo);
    //gfn_CallService(SOBJ_Claim, DOBJ_UserInfo, MethodObj);
    */
	var MethodObj = new callService("com.inswave.admin.task.AuthTask", "getUserInfo");
	MethodObj.setId("getUserInfo");
	MethodObj.setParam("sUserID",sEmpNo);
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/hashtable/data/ADMAB01");
	MethodObj.addXPath("//*/AUTHLIST");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	Ds_Detail.setJSON (_tranSync.getDsJson("ADMAB01"));     
	DsWs_Detail.setJSON (_tranSync.getDsJson("ADMAB01"));     
	DsWs_DetailAuth.setJSON (_tranSync.getDsJson("AUTHLIST"));

    //wconv.copy(Ds_Detail, "DsWs_Detail");
    //wconv.trace(replace(gfn_XmlEncoder(DOBJ_UserInfo.Contents), "><", ">\n<"));
    //wconv.trace(wconv.saveXml(DsWs_DetailAuth));
    for (var i = 0; i < DsWs_DetailAuth.getRowCount(); i++) {
        var nCurAuthId = DsWs_DetailAuth.getCellData(i, "nAuthID");
        var nCurRow = wconv.findRow(DsWs_AuthList, "nAuthID", nCurAuthId);
        DsWs_DetailAuth.setCellData(i, "sAuthName", DsWs_AuthList.getCellData(nCurRow, "sAuthName"));
        DsWs_DetailAuth.setCellData(i, "sDepartName", DsWs_AuthList.getCellData(nCurRow, "sDepartName"));
        DsWs_DetailAuth.setCellData(i, "sDescription", DsWs_AuthList.getCellData(nCurRow, "sDescription"));
    }
    wconv.clear(DsWs_SelPosition);
    /*
    var MethodObj = gfn_CreateMethodObj(SOBJ_Claim, "ClaimAdminTask", "getAllSelPosition");
    gfn_CallService(SOBJ_Claim, DOBJ_SelPosition, MethodObj);
    */
	var MethodObj = new callService("com.inswave.claim.task.ClaimAdminTask", "getAllSelPosition");
	MethodObj.setId("getAllSelPosition");
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAC01");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_SelPosition.setJSON (_tranSync.getDsJson("ADMAC01"));  

    //DsWs_SelPosition.sort("sLevel");
    var insRow = util.insertRow(DsWs_SelPosition, 0);
    DsWs_SelPosition.setCellData(0, "sLevel", "0");
    DsWs_SelPosition.setCellData(0, "sDepartName", "전  체");
    DsWs_SelPosition.setCellData(0, "sDepartCode", "0000000");

    wconv.copy(Ds_SelPosition, "DsWs_SelPosition");
    wconv.copy(Ds_SelPosition2, "DsWs_SelPosition");
    wconv.copy(Ds_SelPosition3, "DsWs_SelPosition");

    scwin.common_channel();

    var dataSetStr = "Ds_Position Ds_Duty"; //해당 데이터셋의 'Editable' 속성은 false로 설정해야 한다.
    var codTypeStr = "86 87";
    var componentIds = "Tab0_tab4_Combo1 Tab0_tab4_Combo3";
    var selectAdds = "Y Y";
    gfn_getCommonCODAA02(dataSetStr, codTypeStr, componentIds, selectAdds);

    //var url = "http://10.1.1.40:9294/hrm/hrm/photo/" + Ds_Detail.getCellData(0, "sEmpNo") + ".jpg";
    //Tab0_tab4_Img_Picture.ImageID = url;
    /*orign
    var MethodObj = gfn_CreateMethodObj(SOBJ_AuthHist, "com.inswave.admin.task.AuthTask", "selectAuthChangeHist", "", "");
    gfn_SetParam(MethodObj, "sUserID", sEmpNo);
    gfn_CallService(SOBJ_AuthHist, DOBJ_AuthHist, MethodObj);
    */
	var MethodObj = new callService("com.inswave.admin.task.AuthTask", "selectAuthChangeHist");
	MethodObj.setId("selectAuthChangeHist");
	MethodObj.setParam("sUserID", sEmpNo);
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAB06");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_AuthHist.setJSON (_tranSync.getDsJson("ADMAB06")); // 
	/*orign
    var MethodObj = gfn_CreateMethodObj(SOBJ_AuthHist, "com.inswave.admin.task.AuthTask", "selectUsrHistory", "", "");
    gfn_SetParam(MethodObj, "sUserID", sEmpNo);
    gfn_CallService(SOBJ_AuthHist, DOBJ_ChgHist, MethodObj);
    */
	var MethodObj = new callService("com.inswave.admin.task.AuthTask", "selectUsrHistory");
	MethodObj.setId("selectUsrHistory");
	MethodObj.setParam("sUserID", sEmpNo);
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/USRHISLIST");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	DsWs_USRHISLIST.setJSON (_tranSync.getDsJson("USRHISLIST")); // dataset 데이터 /ui/cbsc/CBSC158S.xml
    wconv.clear(DsWs_USRHISLISTDETAIL); 

    //20130312 변경이력 순번DESC sort
    //DsWs_USRHISLIST.Sort("nSeq", false);

    util.setRowPosition( Ds_Detail ,0);    
};

scwin.common_channel = function() {
    wconv.clear(Ds_SalesChannel);
    wconv.clear(Ds_SubChannel);
    wconv.clear(Ds_SubChannel2);
    /*orign
    //SOBJ_Admin.WSDL = gds.gWsdlClaim;
    var MethodObj = gfn_CreateMethodObj(SOBJ_Admin, "com.inswave.admin.task.AdminTask", "getCommonCodeList");
    gfn_CallService(SOBJ_Admin, DOBJ_CODAA14, MethodObj);
    */
	var MethodObj = new callService("com.inswave.admin.task.AdminTask", "getCommonCodeList");
	MethodObj.setId("getCommonCodeList");
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/hashtable/data[@hashkey='CODAA14DETAIL']/vector/data/CODAA14");
	MethodObj.addXPath("//*/hashtable/data[@hashkey='CODAA14COMMON']/vector/data/CODAA14");	                              
	MethodObj.addXPath("//WARNING");
	MethodObj.setXPathDs();
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_SubChannel.setJSON (_tranSync.getDsJson("//*/hashtable/data[@hashkey='CODAA14DETAIL']/vector/data/CODAA14"));
