scwin.Tab0_tab1_Radio0_OnChanged = function(info){
    //trace(Tab0.tab1.Radio0.Index);
    var nIdx = Tab0_tab1_Radio0.getSelectedIndex();
    if (nIdx == 0) {
        DsWs_UserAuth.removeColumnFilterAll();
    } else if (nIdx == 1) {
        wconv.filter(DsWs_UserAuth, "nAuthGroupID!==0",true);
    } else if (nIdx == 2) {
        wconv.filter(DsWs_UserAuth, "nAuthGroupID==0",true);
    }
};

scwin.cm_btnExDown = function() {
    if (Tab0.getSelectedTabIndex() == 0) {
        if (DsWs_UserAuth.getTotalRow() < 1) {
            wprom.alert("엑셀출력할 목록이 존재하지 않습니다.");
            return;
        }
        //Tab0_tab1_Grid0.SetColProp(10, "Width", 50);
        //Tab0_tab1_Grid0.SetColProp(12, "Width", 50);
        //gfn_ExportExcel(Tab0_tab1.Grid0, "부서별 사용자 권한");
        Tab0_tab1_Grid0.setColumnWidth( 10 , 50 )
        Tab0_tab1_Grid0.setColumnWidth( 12 , 50 )
		var option={"fileName": "부서별 사용자 권한.xlsx"};
		var infoArr = {};
		common.downLoadExcel("Tab0_tab1_Grid0",option,infoArr);	
        Tab0_tab1_Grid0.setColumnWidth( 10 , 0 )
        Tab0_tab1_Grid0.setColumnWidth( 12 , 0 )
        //Tab0_tab1_Grid0.SetColProp(10, "Width", 0);
        //Tab0_tab1_Grid0.SetColProp(12, "Width", 0);
    } else if (Tab0.getSelectedTabIndex() == 2) {
        if (DsWs_MenuList.getTotalRow() < 1) {
            wprom.alert("엑셀출력할 목록이 존재하지 않습니다.");
            return;
        }
		var option={"fileName": "부서별 메뉴ID"};
		var infoArr = {};
		common.downLoadExcel("Tab0_tab3_Grid0",option,infoArr);	
        //gfn_ExportExcel(Tab0_tab3.Grid0, "부서별 메뉴ID");
    }
};

scwin.view_userdetail = function(nUserID, sUserName) {
    Tab0.setSelectedTabIndex(1);
    Tab0_tab4_Edit10.setValue( nUserID);
    Tab0_tab4_Edit12.setValue(sUserName);
    scwin.lfn_Search();
};

scwin.popup_userMenuByAuth = function(nAuthID, sAuthName, sDepartCode, sDepartName, sDescription, sApplySystem) {
	//wprom.alert("추후개발예정");
	//return ;

    var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
    	var sArgs = {
        	"sAuthID" : nAuthID  
            ,"sAuthName" : sAuthName 
        	,"sDepartCode" : sDepartCode 
        	,"sDepartName" : sDepartName 
        	,"sDescription" : sDescription 
        	,"sApplySystem" : sApplySystem
    	 };
		var popInfo = {
				"width"     : 834,
				"height"    : 470,
				"indata"    : sArgs,
				"callback"  : "",
				"title"     : "권한별사용자및 메뉴목록"
		};     	
        return wprom.popupOpen("/ui/cbso/CBSO038P.xml",popInfo); 

    });
    promise = promise.then(function(rst) {
        if (wconv.length(rst) != 0) {
            var arrRst = rst.split( "^");
            //for (var i = 0; i < wconv.length(arrRst); i++)
                //wconv.trace("arrRst[" + i + "] : " + arrRst[i]);

        }
        return;
    });
    return promise;
};

scwin.Tab0_tab3_Tab0_tab1_Grd_Auth_OnCellClick = function(nRow,col){

    if (col == 3) {
        if (DsWs_DetailAuth.getCellData(nRow, "nAuthID") != "") {
            scwin.popup_userMenuByAuth(DsWs_DetailAuth.getCellData(nRow, "nAuthID")
                                                     , DsWs_DetailAuth.getCellData(nRow, "sAuthName")
                                                     , DsWs_DetailAuth.getCellData(nRow, "sDepartCode")
                                                     , DsWs_DetailAuth.getCellData(nRow, "sDepartName")
                                                     , DsWs_DetailAuth.getCellData(nRow, "sDescription"));
        }
    }

};
