scwin.cmb_sApplySystem_OnChanged_Sync = function(info){
    ds_menu1.removeAll();
    //메뉴처리
    /*
    var MethodObj = gfn_CreateMethodObj(SOBJ_Admin, "com.inswave.admin.task.AuthTask", "selectMenuList");
    gfn_SetParam(MethodObj, "sApplySystem", Tab0_tab3_cmb_sApplySystem.getValue());
    gfn_CallService(SOBJ_Admin, DOBJ_MENU, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.admin.task.AuthTask", "selectMenuList");
	MethodObj.setId("selectMenuList");
	MethodObj.setParam("sApplySystem", Tab0_tab3_cmb_sApplySystem.getValue());
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/MENULIST");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_Menu.setJSON (_tranSync.getDsJson("MENULIST")); // dataset 데이터 

    wconv.copy(ds_menu1, "DsWs_Menu");
    wconv.filter(ds_menu1, "sMenuDepth=='1' && sApplySystem=='" + Tab0_tab3_cmb_sApplySystem.getValue() + "'",true);
    //wconv.copyF(ds_menu1, "DsWs_Menu");

    var insRow = util.insertRow(ds_menu1, 0);
    ds_menu1.setCellData(0, "sMenuName1", "선택");
    Tab0_tab3_cmb_sMenu1.setSelectedIndex( 0 );

    scwin.cmb_sMenu1_OnChanged();
};

