scwin.Tab0_tab3_Grid0_OnCellClick = function(obj, nRow, nCell, nX, nY, nPivotIndex) {
    var promise = Promise.resolve();
    if (DsWs_MenuList.getCellData(nRow, "nAuthID") != "") {
        if (nCell == 8) {
            promise = promise.then(function(rtnVal) {
                return scwin.popup_userMenuByAuth(DsWs_MenuList.getCellData(nRow, "nAuthID"), DsWs_MenuList.getCellData(nRow, "sAuthName"), DsWs_MenuList.getCellData(nRow, "sDepartCode"), DsWs_MenuList.getCellData(nRow, "sDepartName"), DsWs_MenuList.getCellData(nRow, "sDescription"), DsWs_MenuList.getCellData(nRow, "sApplySystem"));
            });
        }
    }
    return promise;
};

