//각 탭에서 관리부서, 사원, 권한그룹, 권한코드 출력처리
scwin.edt_OnKeyDown = function(e,nKey) 
{
    var promise = Promise.resolve();
    var popupName = "";
    var args = "";
    var flags = "";
    if (e.keyCode == 13||nKey==13) {
        if (this == Tab0_tab1_Edit1) {
            //사용자권한 관리부서
            agrs = "";
            if (wconv.length(wconv.trim(Tab0_tab1_Edit1.getValue())) == 7 || agf_isNum(Tab0_tab1_Edit1.getValue()) == false) {
                flags = scwin.agf_DeptCode(Tab0_tab1_Edit1.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab1_Edit1.setValue( arrt[0] );
                    Tab0_tab1_Edit4.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우
                    args = "psButton='' pCode='" + Tab0_tab1_Edit1.getValue() + "'";
                    scwin.Tab0_tab1_btn_Pop_Search_OnClick(Tab0_tab1_btn_Pop_Search, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab1_Edit4.setValue("");
                    return;
                }
            } else {
                Tab0_tab1_Edit4.setValue("");
            }
        } else if (this == Tab0_tab1_Edit2) {
            //사용자권한 사원
            args = "";
            if (wconv.length(wconv.trim(Tab0_tab1_Edit2.getValue())) == 7 || agf_isNum(Tab0_tab1_Edit2.getValue()) == false) {
                flags = scwin.agf_EmpNo(Tab0_tab1_Edit2.getValue());

                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab1_Edit2.setValue( arrt[0] );
                    Tab0_tab1_Edit5.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우
                    args = "psButton='' pCode='" + Tab0_tab1_Edit2.getValue() + "'";
                    scwin.Tab0_tab1_Button1_OnClick(Tab0_tab1.Button1, args);
                    return;
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab1_Edit5.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab1_Edit5.setValue( "" );
            }
        } else if (this == Tab0_tab1_Edit0) {
            //사용자권한 권한그룹
            args = "";
            if (wconv.length(wconv.trim(Tab0_tab1_Edit0.getValue())) > 4 || agf_isNum(Tab0_tab1_Edit0.getValue()) == false) {
                flags = scwin.getAutgGroup(Tab0_tab1_Edit0.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab1_Edit0.setValue( arrt[0] );
                    Tab0_tab1_Edit7.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우
                    args = "sType=group";
                    args += " sAuthGroupName='" + Tab0_tab1_Edit0.getValue() + "'";
                    scwin.Tab0_tab3_btn_Pop_Search_OnClick(Tab0_tab1.Button0, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab1_Edit7.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab1_Edit7.setValue( "" );
            }
        } else if (this == Tab0_tab1_Edit3) {
            //사용자권한 권한코드
            args = "";
            if (wconv.length(wconv.trim(Tab0_tab1_Edit3.getValue())) > 4 || agf_isNum(Tab0_tab1_Edit3.getValue()) == false) {
                flags = scwin.getAuth(Tab0_tab1_Edit3.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab1_Edit3.setValue( arrt[0] );
                    Tab0_tab1_Edit6.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우				 
                    args += " sAuthName='" + Tab0_tab1_Edit3.getValue() + "'";
                    scwin.Tab0_tab1_Button2_OnClick(Tab0_tab1.Button2, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab1_Edit6.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab1_Edit6.setValue( "" );
            }
        } else if (this == Tab0_tab4_Edit10) {
            //사용자상세 사원
            args = "";
            if (wconv.length(wconv.trim(Tab0_tab4_Edit10.getValue())) == 7 || agf_isNum(Tab0_tab4_Edit10.getValue()) == false) {
                flags = scwin.agf_EmpNo(Tab0_tab4_Edit10.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab4_Edit10.setValue( arrt[0] );
                    Tab0_tab4_Edit12.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우
                    args = "psButton='' pCode='" + Tab0_tab4_Edit10.getValue() + "'";
                    scwin.Tab0_tab4_Button1_OnClick(Tab0_tab4_Button1, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab4_Edit12.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab4_Edit12.setValue( "" );
            }
        } else if (this == Tab0_tab3_Edit1) {
            //메뉴권한 관리부서
            agrs = "";
            if (wconv.length(wconv.trim(Tab0_tab3_Edit1.getValue())) == 7 || agf_isNum(Tab0_tab3_Edit1.getValue()) == false) {
                flags = scwin.agf_DeptCode(Tab0_tab1_Edit1.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab3_Edit1.setValue( arrt[0] );
                    Tab0_tab3_Edit4.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우
                    args = "psButton='' pCode='" + Tab0_tab3_Edit1.getValue() + "'";
                    scwin.Tab0_tab1_btn_Pop_Search_OnClick(Tab0_tab3.btn_Pop_Search, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab3_Edit4.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab3_Edit4.setValue( "" );
            }
        } else if (this == Tab0_tab3_Edit3) {
            //메뉴권한 권한코드
            args = "";
            if (wconv.length(wconv.trim(Tab0_tab3_Edit3.getValue())) > 4 || agf_isNum(Tab0_tab3_Edit3.getValue()) == false) {
                flags = scwin.getAuth(Tab0_tab3_Edit3.getValue());
                if (flags != "list" && flags != "0") {
                    //조회가 1건인경우
                    var arrt = flags.split("^");
                    Tab0_tab3_Edit3.setValue( arrt[0] );
                    Tab0_tab3_Edit6.setValue( arrt[1] );
                    return;
                } else if (flags == "list") {
                    //2건이상인 경우				 
                    args += " sAuthName='" + Tab0_tab3_Edit3.getValue() + "'";
                    scwin.Tab0_tab1_Button2_OnClick(Tab0_tab3.Button2, args);
                } else if (flags == "0") {
                    //결과가 없는경우
                    Tab0_tab3_Edit6.setValue( "" );
                    return;
                }
            } else {
                Tab0_tab3_Edit6.setValue( "" );
            }
        }
    }
    //return promise;
};

//관리부서 처리
scwin.agf_DeptCode = function(param) {
    var rtn = "";
    var lsBaseDate = wconv.today();
    var lsLevel = "A";
    /*
    SOBJ.WSDL = gds.gWsdlAccounting;
    DsWs_CodeSelect.XPath = "//-/vector/data/ADMAC01";
    wconv.clear(DsWs_CodeSelect);
    var MethodObj = gfn_CreateMethodObj(SOBJ, "com.inswave.admin.task.AdminTask", "getNewDepartbyLevel", "9999", "1");
    gfn_SetParam(MethodObj, "sBaseDate", lsBaseDate); // 기준일자
    gfn_SetParam(MethodObj, "sLevel", lsLevel); // 부서레벨
    if (agf_isNum(param)) {
        gfn_SetParam(MethodObj, "sCenterCode", param); // 센타팀코드
    } else {
        gfn_SetParam(MethodObj, "sCenterName", param); // 부서명
    }
    // 조회실행
    gfn_CallService(SOBJ, DOBJ_ADCOM, MethodObj);
    */
    wconv.clear(DsWs_CodeSelect);
	var MethodObj = new callService("com.inswave.admin.task.AdminTask", "getNewDepartbyLevel", "9999", "1");
	MethodObj.setId("getNewDepartbyLevel");
	MethodObj.setParam("sBaseDate", lsBaseDate);
	MethodObj.setParam("sLevel", lsLevel);
    if (agf_isNum(param)) {
        MethodObj.setParam( "sCenterCode", param); // 센타팀코드
    } else {
        MethodObj.setParam( "sCenterName", param); // 부서명
    }    
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAC01");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	DsWs_CodeSelect.setJSON (_tranSync.getDsJson("ADMAC01")); // dataset 데이터 

    if (DsWs_CodeSelect.getRowCount() == 1) {
        rtn = DsWs_CodeSelect.getCellData(0, "sCenterCode") + "^" + DsWs_CodeSelect.getCellData(0, "sCenterName");
    } else if (DsWs_CodeSelect.getRowCount() > 1) {
        rtn = "list";
    } else if (DsWs_CodeSelect.getRowCount() == 0) {
        rtn = "0";
    }
    return rtn;
};

