    if (DsWs_CodeSelect.getRowCount() == 1) {
        rtn = DsWs_CodeSelect.getCellData(0, "sEmpNo") + "^" + DsWs_CodeSelect.getCellData(0, "sEmpName");
    } else if (DsWs_CodeSelect.getRowCount() > 1) {
