        gfn_SetParam(MethodObj, "nAuthID", param); // 그룹권한코드
    } else {
        gfn_SetParam(MethodObj, "sAuthName", param); // 그룹권한명
    }
    gfn_CallService(SOBJ, DOBJ_ADCOM, MethodObj);
    */
    wconv.clear(DsWs_CodeSelect);
	var MethodObj = new callService("com.inswave.claim.task.ClaimAdminTask", "getAllAuth", "9999", "1");
	MethodObj.setId("getAllAuth");
    MethodObj.setParam( "sAuthType", "A"); // 센타팀코드
    if (agf_isNum(param)) {
        MethodObj.setParam( "nAuthID", param); // 센타팀코드
    } else {
        MethodObj.setParam( "sAuthName", param); // 부서명
    }    
	MethodObj.setUrl("/common/CBSO014MController.do");
	MethodObj.setXPath("//*/vector/data/ADMAA13");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_CodeSelect.setJSON (_tranSync.getDsJson("ADMAB01")); // dataset 데이터    
    //wconv.trace(replace(gfn_XmlEncoder(DOBJ_ADCOM.Contents), "><", ">\n<"));
    if (DsWs_CodeSelect.getRowCount() == 1) {
        rtn = DsWs_CodeSelect.getCellData(0, "nAuthID") + "^" + DsWs_CodeSelect.getCellData(0, "sAuthName");
    } else if (DsWs_CodeSelect.getRowCount() > 1) {
        rtn = "list";
    } else if (DsWs_CodeSelect.getRowCount() == 0) {
        rtn = "0";
    }
    return rtn;
};

