//사용화면및 캡처대상
scwin.Tab0_tab4_Button0_OnClick = function(obj) {
    wprom.alert("추후개발예정");
    return ; 

    var promise = Promise.resolve();
    if (Tab0_tab4_Edit10.getValue() == "" || wconv.trim(Tab0_tab4_Edit10.getValue()) == "") {
        wprom.alert("사원번호를 입력하세요.");
        return;
    }
    promise = promise.then(function(rtnVal) {
    	var sArg = {"sEmpNo":Tab0_tab4_Edit10.getValue()
    	               ,"sEmpName":Tab0_tab4_Edit12.getValue()
    					};
		var popInfo = {
				"width"     : 835,
				"height"    : 470,
				"indata"    : sArg,
				"callback"  : "",
				"title"     : "사용화면및 캡처대상"
			};    					
        return wprom.popupOpen("/ui/cbso/CBSO037P.xml", popInfo);//9951_pop.xml
    });
    return promise;
};
