    //WebBrowser0.PageUrl = sUrl;
    //WebBrowser0.Run();
};	
//
//expression function
// 
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) { 

	var sRslt = ""; 
	if (Tab0_tab4_Tab0_tab3_Grd_Auth.getColumnID(colIndex)  == "sInputDate") {  // 
		var sInputDate = DsWs_USRHISLIST.getCellData(rowIndex, "sInputDate");   // 
		var sInputTime = DsWs_USRHISLIST.getCellData(rowIndex, "sInputTime");
		sRslt = sInputDate.substr(0,4)+"-"+sInputDate.substr(4,2)+"-"+sInputDate.substr(6,2)+" "
		       + sInputTime.substr(0,2)+":"+	sInputTime.substr(2,2)+":"+sInputTime.substr(4,2);
	}
	return sRslt; 
};
//
//expression function 
scwin.lfn_getExprAuthGroupID = function(data, formattedData, rowIndex, colIndex) {
	 var sRslt = ""; 
	 if(DsWs_DetailAuth.getCellData( rowIndex , "nAuthGroupID" ) == "0"){
		sRslt = "";
	}	else{
		sRslt = DsWs_DetailAuth.getCellData( rowIndex , "nAuthGroupID" )
	}

	return sRslt;
};
//expression function 
scwin.lfn_getExprUpdateType = function(data, formattedData, rowIndex, colIndex) {
	var sUpdateType = DsWs_AuthHist.getCellData(rowIndex,"sUpdateType");
	var result;
	switch(sUpdateType){
		case "00":	result = "변경없음";				break;
		case "01":	result = "직책변경";				break;	
		case "02":	result = "소속변경";				break;	
		case "03":	result = "직책 및 소속변경";	break;	
		case "04":	result = "퇴사";					break;	
		case "05":	result = "직위변경";				break;
		default:      result = ""; 						break;	
	}
	return result;
};
